import React, { useState, useEffect } from 'react';
import FetchStockList from './FetchStockList';
import FetchStockData from './FetchStockData';
import './Home.css';

function Home() {
    const [stockList, setStockList] = useState([]);
    const [stockData, setStockData] = useState([]);
    const [filteredData, setFilteredData] = useState([]);

    useEffect(() => {
        if (stockList.length > 0 && stockData.length > 0) {
            const latestDataMap = new Map();

            // stockData에서 가장 최근 데이터만 추출
            stockData.forEach((data) => {
                const existingData = latestDataMap.get(data.stock_name);
                if (!existingData || new Date(existingData.date) < new Date(data.date)) {
                    latestDataMap.set(data.stock_name, data);
                }
            });

            // stockList와 매칭되는 종목 필터링
            const filtered = stockList.filter(stock => latestDataMap.has(stock.stock_name)).map(stock => latestDataMap.get(stock.stock_name));

            setFilteredData(filtered);
        }
    }, [stockList, stockData]);

    return (
        <div className='home-container'>
            <FetchStockList onListFetch={setStockList} />
            <FetchStockData onDataFetch={setStockData} />

            <div className='table-container'>
                <table className='table'>
                    <thead>
                        <tr>
                            <th>종목명</th>
                            <th>일자</th>
                            <th>종가</th>
                            <th>고가</th>
                            <th>저가</th>
                            <th>시가</th>
                            <th>거래량</th>
                        </tr>
                    </thead>
                    <tbody>
                        {filteredData.map((data, index) => (
                            <tr key={index}>
                                <td>{data.stock_name}</td>
                                <td>{data.date}</td>
                                <td>{data.close}</td>
                                <td>{data.high}</td>
                                <td>{data.low}</td>
                                <td>{data.open}</td>
                                <td>{data.volume}</td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
        </div>
    );
}

export default Home;
