import React, { useState, useEffect } from "react";
import Highcharts from "highcharts";
import HighchartsReact from "highcharts-react-official";
import FetchStockDataForCode from "../ComponentsFetch/FetchStockDataForCode";
import FetchParabolicSARData from "../ComponentsFetch/FetchParabolicSARData";
import HighchartsStock from "highcharts/modules/stock";  // Highcharts Stock 모듈

// Highcharts Stock 모듈 활성화
HighchartsStock(Highcharts);


function ChartOfSAR({ stockCode }) {
    const [stockData, setStockData] = useState([]);
    const [stockSAR, setStockSAR] = useState([]);
    const [options, setOptions] = useState({});

    useEffect(() => {
        if (stockData.length > 0 && stockSAR.length > 0 && stockCode) {
            const dataForChart = stockData
                .map(item => ({
                    date: new Date(item.date).getTime(), // timestamp로 변경
                    close: item.close,
                }))
                .sort((a, b) => new Date(a.date) - new Date(b.date));

            // Parabolic SAR 데이터를 종가 데이터와 매칭하여 동일한 날짜에 표시
            const sarForChart = stockSAR
                .map(item => ({
                    date: new Date(item.date).getTime(), // timestamp로 변경
                    sar: item.Parabolic_SAR,
                }))
                .sort((a, b) => new Date(a.date) - new Date(b.date));

            // Set chart options
            setOptions({
                chart: {
                    type: 'line',
                    zoomType: 'x', // x축을 기준으로 확대 가능
                    height: 400,
                },
                title: {
                    text: ``,
                },
                xAxis: {
                    type: 'datetime', // datetime 유형으로 변경
                    labels: {
                        format: '{value:%m-%d}', // 날짜 형식
                    },
                    opposite: true,
                },
                yAxis: {
                    title: {
                        text: '',
                    },
                },
                series: [
                    {
                        name: 'Close Price',
                        data: dataForChart.map(item => [item.date, item.close]),
                        marker: {
                            enabled: false, // Disable data point markers for close price
                        },
                        color: '#0071e3',
                        lineWidth: 1,
                    },
                    {
                        name: 'Parabolic SAR',
                        type: 'scatter', // Scatter plot for SAR
                        data: sarForChart.map(item => [item.date, item.sar]),
                        marker: {
                            symbol: 'plus',
                            radius: 1, // 점 크기 설정
                        },
                        color: '#4B4B4B', // 점 색상 설정 (Parabolic SAR 색상)
                        tooltip: {
                            pointFormat: 'Parabolic SAR: <b>{point.y}</b>',
                        },
                    },
                ],
                legend: {
                    enabled: true, // Show legend to distinguish between Close Price and Parabolic SAR
                },
                credits: {
                    enabled: false, // Disable the "Highcharts.com" text
                },
                tooltip: {
                    shared: true,
                    crosshairs: true,
                },
                plotOptions: {
                    series: {
                        states: {
                            inactive: {
                                opacity: 1 // 마우스를 올렸을 때 다른 라인의 투명도를 1로 설정해 희미해지지 않도록 설정
                            },
                            hover: {
                                enabled: true // 마우스 호버 시 희미해지는 기능 비활성화
                            }
                        }
                    }
                },
                scrollbar: {
                    enabled: true, // 하단 스크롤바 활성화
                },
                rangeSelector: {
                    enabled: true, // 범위 선택 옵션 활성화
                    inputEnabled: false,
                    buttons: [
                        {
                            type: 'month',
                            count: 1,
                            text: '1m'
                        },
                        {
                            type: 'month',
                            count: 3,
                            text: '3m'
                        },
                        {
                            type: 'month',
                            count: 6,
                            text: '6m'
                        },
                        {
                            type: 'ytd',
                            text: 'YTD'
                        },
                        {
                            type: 'year',
                            count: 1,
                            text: '1y'
                        },
                        {
                            type: 'all',
                            text: 'All'
                        }
                    ],
                    selected: 5 // Default 선택 범위 설정 (3개월)
                },
            });
        }
    }, [stockData, stockSAR, stockCode]);

    if (!stockCode) return null;

    return (
        <div>
            <FetchStockDataForCode stockCode={stockCode} onSDFCFetch={setStockData} />
            <FetchParabolicSARData stockCode={stockCode} onSARFetch={setStockSAR} />
            <HighchartsReact highcharts={Highcharts} options={options} />
        </div>
    );
}

export default ChartOfSAR;
