import React, { useState, useEffect } from "react";
import FetchRSIData from "../ComponentsFetch/FetchRSIData";
import FetchStockDataForCode from "../ComponentsFetch/FetchStockDataForCode";
import FetchStockList from "../ComponentsFetch/FetchStockList";
import Highcharts from "highcharts";
import HighchartsReact from "highcharts-react-official";
import HighchartsStock from "highcharts/modules/stock"; // Highcharts Stock 모듈 추가

HighchartsStock(Highcharts);


function ChartOfRSI({ stockCode }) {
    const [stockData, setStockData] = useState([]);
    const [stockList, setStockList] = useState([]);
    const [rsiData, setRsiData] = useState([]);
    const [options, setOptions] = useState({});

    useEffect(() => {
        if (stockList.length > 0 && stockData.length > 0 && rsiData.length > 0) {

            const formattedRSIData = rsiData.map(item => {
                return [
                    new Date(item.date).getTime(),
                    item.RSI
                ];
            });

            const formattedClosePriceData = stockData.map(item => ({
                x: new Date(item.date).getTime(),
                y: item.close
            }));

            const formattedVolumeData = stockData.map(item => ({
                x: new Date(item.date).getTime(),
                y: item.volume,
            }));


            setOptions({
                chart: {
                    height: 600,
                    zoomType: 'x',
                },
                title: {
                    text: ''
                },
                accessibility: {
                    enabled: false
                },
                xAxis: {
                    type: 'datetime',
                    crosshair: true,
                },
                yAxis: [
                    {
                        title: {
                            text: 'Close Price',
                            style: {
                                fontSize: '12px',
                            },
                            rotation: 0,
                        },
                        height: '50%',
                        lineWidth:2,
                        opposite: true,
                    },
                    {
                        title: {
                            text: 'RSI(30, 70)',
                            style: {
                                fontSize: '12px',
                            },
                            rotation: 0,
                        },
                        top: '72%',
                        height: '28%',
                        offset:0,
                        lineWidth:2,
                        labels:{
                            enabled:false,
                        },
                        opposite: true,
                    },
                    {
                        title: {
                            text: 'volume',
                            
                        }
                    }
                ],
                series: [{
                    name: 'RSI',
                    marker: {
                        enabled: false
                    },
                    lineWidth: 1,  // 선 굵기 설정
                    color: '#8E44AD',  // 선 색상 설정 (보라색 계열)
                    data: formattedRSIData,
                    tooltip: {
                        valueDecimals: 2 // 소수점 2자리로 표시
                    }
                }],
                legend: {
                    enabled: false // 범례 숨기기
                },
                credits: {
                    enabled: false // "Highcharts.com" 표시 비활성화
                },
                tooltip: {
                    shared: true,
                    crosshairs: true
                },
            });
        }
    }, [stockList, stockData, rsiData, stockCode]);

    return (
        <div>
            <FetchStockDataForCode stockCode={stockCode} onSDFCFetch={setStockData} />
            <FetchStockList onListFetch={setStockList} />
            <FetchRSIData stockCode={stockCode} onRSIFetch={setRsiData} />

            {options.series && options.series.length > 0 && (
                <HighchartsReact highcharts={Highcharts} options={options} />
            )}
        </div>
    );
}

export default ChartOfRSI;
