import React, {useEffect, useState} from "react";
import FetchRSIData from "./FetchRSIData";

function RSIAnalysis1({ stockCode }){
    const [RsiData, setRsiData] = useState(null)
    const [recentRsiValue, setRecentRsiValue] = useState({ value: null, date: null });

    const handleTodayRsi = (data) => {
        setRsiData(data);
    };

    useEffect(() => {
        if (RsiData && RsiData.length > 0) {
            const recentRsiData = RsiData[RsiData.length - 1];
            const recentRsiDate = recentRsiData.date;
            const newRecentRsiValue = recentRsiData.RSI.toFixed();


            if (recentRsiValue.value !== newRecentRsiValue || recentRsiDate.date !== newRecentRsiValue) {
                setRecentRsiValue({ value: newRecentRsiValue, date: recentRsiDate });
            }
        }
    }, [RsiData, {recentRsiValue, setRecentRsiValue}]);

    return(
        <div>
            <FetchRSIData stockCode={stockCode} onRSIFetch={handleTodayRsi} />
            {recentRsiValue !== null && (
                <p>{recentRsiValue.date}의 RSI의 값은 {recentRsiValue.value}.tofixe 입니다.</p>
            )}
        </div>
    )
}

export default RSIAnalysis1
