import React, { useState, useEffect } from "react";
import Highcharts from "highcharts";
import FetchStockDataForCode from "../ComponentsFetch/FetchStockDataForCode";
import HighchartsReact from "highcharts-react-official";
import HighchartsMore from "highcharts/highcharts-more"; // highcharts-more 모듈 추가
import HighchartsStock from "highcharts/modules/stock";  // Highcharts Stock 모듈 추가

// 모듈 활성화
HighchartsMore(Highcharts);
HighchartsStock(Highcharts);

function ChartOfClose({ stockCode }) {
    const [stockData, setStockData] = useState([]);
    const [options, setOptions] = useState({});
    const [chartType, setChartType] = useState('line'); // 차트 유형 상태 추가 ('line'이 기본값)

    useEffect(() => {
        if (stockData.length > 0 && stockCode) {
            const dataForChart = stockData
                .map(item => ({
                    date: new Date(item.date).getTime(),
                    open: item.open,
                    high: item.high,
                    low: item.low,
                    close: item.close,
                }))
                .sort((a, b) => a.date - b.date);

            // Set chart options based on chartType
            setOptions({
                chart: {
                    type: chartType, // chartType 상태에 따라 차트 유형 변경
                    zoomType: 'x',
                    height: 330,
                },
                title: {
                    text: '',
                },
                accessibility: {
                    enabled: false // 접근성 모듈 비활성화
                },
                xAxis: {
                    type: 'datetime', // datetime 유형으로 변경
                    labels: {
                        format: '{value:%m-%d}', // 날짜 형식
                    },
                },
                yAxis: {
                    title: {
                        text: '',
                    },
                    lineWidth: 1,
                    opposite: true
                },
                series: [
                    {
                        name: chartType === 'line' ? 'Close Price' : 'Candlestick',
                        data: chartType === 'line'
                            ? dataForChart.map(item => [item.date, item.close])
                            : dataForChart.map(item => [item.date, item.open, item.high, item.low, item.close]),
                        marker: {
                            enabled: chartType === '', // Line chart에서만 마커 표시
                        },
                        color: chartType === 'line' ? '#0071e3' : undefined, // Line 차트의 색상
                        type: chartType, // 차트 유형 설정
                        pointWidth: chartType === 'candlestick' ? 3 : undefined, // 캔들의 너비를 수동으로 조정 (값을 줄이면 얇아짐)
                        upColor: '#0071e3', // 상승 캔들의 색상
                        color: '#d32f2f', // 하락 캔들의 색상
                    },
                ],
                legend: {
                    enabled: false,
                },
                credits: {
                    enabled: false, // Disable the "Highcharts.com" text
                },
                tooltip: {
                    shared: true,
                    crosshairs: true,
                    valueDecimals: 2,
                },
                scrollbar: {
                    enabled: true, // 하단 스크롤바 활성화
                },
                rangeSelector: {
                    enabled: true, // 범위 선택 옵션 활성화
                    inputEnabled: false,
                    buttons: [
                        {
                            type: 'month',
                            count: 1,
                            text: '1m'
                        },
                        {
                            type: 'month',
                            count: 3,
                            text: '3m'
                        },
                        {
                            type: 'month',
                            count: 6,
                            text: '6m'
                        },
                        {
                            type: 'ytd',
                            text: 'YTD'
                        },
                        {
                            type: 'year',
                            count: 1,
                            text: '1y'
                        },
                        {
                            type: 'all',
                            text: 'All'
                        }
                    ],
                    selected: 5 // Default 선택 범위 설정 (3개월)
                },
            });
        }
    }, [stockData, stockCode, chartType]); // chartType이 변경될 때마다 차트 업데이트

    if (!stockCode) return null;

    return (
        <div>
            <div>
                <button onClick={() => setChartType('line')}>Line</button>
                <button onClick={() => setChartType('candlestick')}>Candle</button>
            </div>
            <FetchStockDataForCode stockCode={stockCode} onSDFCFetch={setStockData} />
            <HighchartsReact highcharts={Highcharts} options={options} />
        </div>
    );
}

export default ChartOfClose;
