import React, { useState, useEffect, useRef } from 'react';
import FetchStockData from "../components/FetchStockData";
import FetchStockList from "../components/FetchStockList";
import ChartOfClose from "../components/ChartOfClose";
import { useNavigate } from 'react-router-dom';
import '../styles/Navbar.css';
import './Home.css';
import ProfileIcon from '../assets/profile.png';

function Home() {
    const [stockData, setStockData] = useState([]);
    const [stockList, setStockList] = useState([]);
    const [filteredData, setFilteredData] = useState([]);
    const [selectedStock, setSelectedStock] = useState(null);
    const [selectedStockName, setSelectedStockName] = useState("");
    const navigate = useNavigate();
    const [isMenuOpen, setIsMenuOpen] = useState(false);
    const menuRef = useRef(null);

    useEffect(() => {
        document.body.classList.add('home');

        // 이벤트 리스너 등록
        document.addEventListener('click', handleClickOutside);

        return () => {
            // 이벤트 리스너 제거
            document.body.classList.remove('home');
            document.removeEventListener('click', handleClickOutside);
        };
    }, []);

    useEffect(() => {
        if(stockList.length > 0 && stockData.length > 0) {
            const latestDataMap = new Map();

            stockData.forEach((data) => {
                const existingData = latestDataMap.get(data.stock_code);
                if (!existingData || new Date(existingData.date) < new Date(data.date)) {
                    const matchingStock = stockList.find(stock => stock.code === data.stock_code);
                    if (matchingStock) {
                        latestDataMap.set(data.stock_code, {
                            ...data,
                            stock_name: matchingStock.name // 이름을 함께 저장
                        });
                    }
                }
            });

            const filtered = stockList
                .filter(stock => latestDataMap.has(stock.code))
                .map(stock => {
                    const data = latestDataMap.get(stock.code);
                    const date = new Date(data.date);
                    const formattedDate = `${(date.getMonth() + 1).toString().padStart(2, '0')}.${date.getDate().toString().padStart(2, '0')}`;
                    return {
                        ...data,
                        date: formattedDate // 날짜 형식을 mm.dd로 변환하여 저장
                    };
                })
                .sort((a, b) => b.volume - a.volume); // 거래량(volume) 기준으로 내림차순 정렬

            setFilteredData(filtered);
        }
    }, [stockList, stockData]);

    const handleStockClick = (stockCode, stockName) => {
        setSelectedStock(stockCode);
        setSelectedStockName(stockName); // 클릭한 종목 이름을 저장
    };

    const toggleMenu = () => {
        setIsMenuOpen(prev => !prev);
    };

    const handleClickOutside = (event) => {
        // 클릭 이벤트를 비동기로 처리하여 메뉴 토글 후 실행되도록 함
        setTimeout(() => {
            if (menuRef.current && !menuRef.current.contains(event.target)) {
                setIsMenuOpen(false);
            }
        }, 0);
    };

    return (
        <>
            <div className='navbar'>
                <h2 className="navbar-title">ProStock</h2>
                <div className="profile-container" onClick={toggleMenu}>
                    <img src={ProfileIcon} alt="Profile" className="profile-icon" />
                    {isMenuOpen && (
                        <div ref={menuRef} className="profile-menu">
                            <p>프로필</p>
                            <p>설정</p>
                            <p>로그아웃</p>
                        </div>
                    )}
                </div>
            </div>

            {/* 광고 영역 추가 */}
            <div className='ad-container'>
                <h2>광고 영역</h2>
            </div>

            <div className='home-container'>
                <FetchStockList onListFetch={setStockList}/>
                <FetchStockData onDataFetch={setStockData}/>

                <div className='table-container'>
                    <table className="table">
                        <thead>
                        <tr>
                            <th>종목명</th>
                            <th>일자</th>
                            <th>종가</th>
                            <th>고가</th>
                            <th>저가</th>
                            <th>시가</th>
                            <th>거래량</th>
                        </tr>
                        </thead>
                        <tbody>
                        {filteredData.map((data, index) => (
                            <tr key={index}>
                                <td className="stock_name"
                                    onClick={() => handleStockClick(data.stock_code, data.stock_name)} style={{
                                    cursor: 'pointer',
                                    color: 'blue',
                                }}>
                                    {data.stock_name}
                                </td>
                                <td>{data.date}</td>
                                <td>{data.close}</td>
                                <td>{data.high}</td>
                                <td>{data.low}</td>
                                <td>{data.open}</td>
                                <td>{data.volume}</td>
                            </tr>
                        ))}
                        </tbody>
                    </table>
                </div>
                <div className="chart-container">
                    {selectedStock && (
                        <>
                            <h3 className="chart-container-text">{selectedStockName} </h3>
                            <ChartOfClose stockCode={selectedStock}/>
                            <button onClick={() => navigate('/detail')} className="more-button"> 더 보기 </button>
                            <button className="like-button"> 즐겨 찾기 </button>
                        </>
                    )}

                </div>
            </div>
        </>
    );
}

export default Home;
