import React, { useEffect, useState } from "react";

function findSupportZones(stockData) {
    // 날짜 순으로 먼저 데이터를 정렬합니다.
    const sortedStockData = [...stockData].sort((a, b) => new Date(a.date) - new Date(b.date));

    let lows = [];
    let firstLow = null;
    let previousHigh = null;

    for (let i = 1; i < sortedStockData.length - 1; i++) {
        // 상승 후 하락하여 저점을 형성
        if (sortedStockData[i - 1].close > sortedStockData[i].close && sortedStockData[i].close < sortedStockData[i + 1].close) {
            // 이전 고점이 있는 경우
            if (previousHigh && firstLow === null) {
                firstLow = { date: sortedStockData[i].date, close: sortedStockData[i].close };
            }
            // 첫 번째 저점이 이미 설정된 경우
            else if (firstLow) {
                const priceDifference = ((firstLow.close - sortedStockData[i].close) / firstLow.close) * 100;
                // 두 번째 저점이 첫 번째 저점보다 낮고, 3% 이상의 차이가 발생할 때만 유효한 두 번째 저점으로 간주
                if (sortedStockData[i].close < firstLow.close && priceDifference >= 3) {
                    lows.push(firstLow);
                    firstLow = { date: sortedStockData[i].date, close: sortedStockData[i].close };
                }
            }
        }

        // 상승 구간을 확인해 고점을 갱신
        if (sortedStockData[i - 1].close < sortedStockData[i].close) {
            previousHigh = sortedStockData[i];
        }
    }

    // 마지막 저점을 추가
    if (firstLow) {
        lows.push(firstLow);
    }

    return lows;
}

function StockAnalysis({ stockData }) {
    const [supportZones, setSupportZones] = useState([]);

    useEffect(() => {
        if (stockData.length > 0) {
            const zones = findSupportZones(stockData);
            setSupportZones(zones);
        }
    }, [stockData]);

    return (
        <div>
            <h3>Support Zones</h3>
            <ul>
                {supportZones.map((zone, index) => (
                    <li key={index}>
                        Date: {zone.date}, Close: {zone.close}
                    </li>
                ))}
            </ul>
        </div>
    );
}

export default StockAnalysis;
