import React, { useState, useEffect, useRef } from 'react';
import FetchTop5Data from "../ComponentsFetch/FetchStockOrignal/FetchTop5Data";

function TechChart() {
    const [topStocks, setTopStocks] = useState([]);  // 상위 5개 종목 상태 추가

    return (
        <>
            <FetchTop5Data onTopFetch={setTopStocks}/>

            <div className="top5-stocks">
                <h3>상위 5개 종목</h3>
                <table className="table">
                    <thead>
                    <tr>
                        <th>순위</th>
                        <th>종목 코드</th>
                        <th>점수</th>
                    </tr>
                    </thead>
                    <tbody>
                    {topStocks.map((stock, index) => (
                        <tr key={stock.stock_code}>
                            <td>{index + 1}</td>
                            <td
                                className="stock_name"
                                onClick={() => handleStockClick(stock.stock_code, stock.stock_code)}
                                style={{
                                    cursor: 'pointer',
                                    color: 'blue',
                                }}
                            >
                                {stock.stock_code}
                            </td>
                            <td>{stock.final_score}</td>
                        </tr>
                    ))}
                    </tbody>
                </table>
            </div>
        </>
    )
}
