import React, { useState, useEffect } from 'react';
import FetchMACDData from '../ComponentsFetch/FetchMACDData';

function MACDAnalysis1({ stockCode }) {
    const [macdData, setMACDData] = useState([]);
    const [analysisResult, setAnalysisResult] = useState('');

    const analyzeMACDCrossovers = (macdData, period = 14) => {
        if (macdData.length < period) {
            return '데이터가 충분하지 않습니다.';
        }

        const lastIndex = macdData.length - 1;
        const currentMACD = macdData[lastIndex].MACD_Line;
        const currentSignal = macdData[lastIndex].Signal_Line;
        const prevMACD = macdData[lastIndex - 1].MACD_Line;
        const prevSignal = macdData[lastIndex - 1].Signal_Line;

        let result = `<strong>현재 MACD</strong><br/>`;
        result += `ㆍMACD: ${currentMACD.toFixed(2)}<br/>`;
        result += `ㆍSignal:${currentSignal.toFixed(2)}<br/><br/>`;

        // 1. 14일간의 MACD 추세 분석
        const macdTrendData = macdData.slice(lastIndex - period + 1, lastIndex + 1).map(data => data.MACD_Line);
        const totalMACDChange = macdTrendData[macdTrendData.length - 1] - macdTrendData[0];
        const macdTrendDirection = totalMACDChange > 0 ? '상승' : '하락';

        // 2. MACD Line과 Signal Line의 크로스 분석
        if (prevMACD < prevSignal && currentMACD > currentSignal) {
            result += '강한 매수 신호: MACD Line이 Signal Line을 아래에서 위로 교차했습니다 (골든 크로스). 상승 추세 가능성이 있습니다.<br/>';
        } else if (prevMACD > prevSignal && currentMACD < currentSignal) {
            result += '강한 매도 신호: MACD Line이 Signal Line을 위에서 아래로 교차했습니다 (데드 크로스). 하락 추세 가능성이 있습니다.<br/>';
        } else {
            result += '현재 MACD와 Signal Line 사이에 특별한 교차 신호가 없습니다.<br/><br/>';
        }

        // 3. Histogram 분석 (추세 강화/약화)
        const currentHistogram = macdData[lastIndex].Histogram;
        const histogramTrend = macdData.slice(lastIndex - period + 1, lastIndex + 1).map(day => day.Histogram);
        const histogramTrendDirection = histogramTrend[histogramTrend.length - 1] - histogramTrend[0];

        if (histogramTrendDirection > 0) {
            result += 'Histogram이 지속적으로 증가하고 있습니다. 모멘텀이 강화되고 있습니다.<br/><br/>';
        } else if (histogramTrendDirection < 0) {
            result += 'Histogram이 지속적으로 감소하고 있습니다. 모멘텀이 약화되고 있습니다.<br/><br/>';
        }

        // 4. MACD 추세 분석에 따른 해석
        if (macdTrendDirection === '상승') {
            result += '14일간의 MACD는 상승 추세입니다. 이는 장기적으로도 긍정적인 모멘텀이 이어질 수 있음을 시사합니다.<br/>';
        } else if (macdTrendDirection === '하락') {
            result += '14일간의 MACD는 하락 추세입니다. 모멘텀이 약해질 수 있으며, 주의가 필요합니다.<br/>';
        }

        return result;
    };

    useEffect(() => {
        if (macdData.length > 0) {
            const result = analyzeMACDCrossovers(macdData);
            setAnalysisResult(result);
        }
    }, [macdData]);

    return (
        <div>
            <FetchMACDData stockCode={stockCode} onMACDFetch={setMACDData} />
            <h4>MACD 크로스오버 및 추세 분석</h4>
            <p dangerouslySetInnerHTML={{ __html: analysisResult }}></p>
        </div>
    );
}

export default MACDAnalysis1;
