import React, { useState, useEffect, useRef } from "react";
import { useNavigate } from 'react-router-dom';
import FetchStockList from "../ComponentsFetch/FetchStockOrignal/FetchStockList";
import FetchRealTime from "../ComponentsFetch/FetchStockOrignal/FetchRealTime";
import ChartOfClose from "../ComponentsChart/ChartOfClose";
import Navbar from "../components/Navbar";
import './Home.css';
import {FixedSizeList as List} from "react-window";
import Select from "react-select";
import FetchStockDataForCode from "../ComponentsFetch/FetchStockOrignal/FetchStockDataForCode"; // 기존 CSS 유지

const MenuList = ({ options, children, maxHeight, getValue }) => {
    const height = 35;
    const [value] = getValue() || [];
    const initialOffset = value ? options.findIndex(option => option.value === value) * height : 0;

    return (
        <List
            height={maxHeight}
            itemCount={children.length}
            itemSize={height}
            initialScrollOffset={initialOffset}
        >
            {({ index, style }) => (
                <div style={style}>
                    {children[index]}
                </div>
            )}
        </List>
    );
};

function Home() {
    const [stockData, setStockData] = useState([]); // 실시간 데이터
    const [stockList, setStockList] = useState([]); // 종목 리스트
    const [filteredData, setFilteredData] = useState([]); // 필터링된 데이터
    const [selectedStock, setSelectedStock] = useState(null); // 선택된 종목
    const navigate = useNavigate();
    const [expandedRows, setExpandedRows] = useState([]); // 확장된 행 관리
    const [availableStocks, setAvailableStocks] = useState([]);
    const [inputValue, setInputValue] = useState("");



    // 종목명 클릭 시 확장된 행을 관리하는 함수
    const handleStockClick = (stockCode) => {
        if (expandedRows.includes(stockCode)) {
            setExpandedRows(expandedRows.filter(row => row !== stockCode)); // 이미 열려 있으면 닫기
        } else {
            setExpandedRows([...expandedRows, stockCode]); // 선택한 종목의 행 열기
            setSelectedStock(stockCode); // 차트를 표시할 종목 설정
        }
    };

    // stockList와 stockData를 조합하여 필터링된 데이터를 생성
    useEffect(() => {
        if (stockList.length > 0 && Object.keys(stockData).length > 0) {
            const formattedData = Object.keys(stockData)
                .map(stockCode => {
                    const matchingStock = stockList.find(stock => stock.code === stockCode);
                    if (matchingStock) {
                        return {
                            ...stockData[stockCode], // stockData에서 실시간 정보를 가져옴
                            stock_code: stockCode, // 종목 코드
                            stock_name: matchingStock.name, // 종목 이름
                        };
                    }
                    return null;
                })
                .filter(data => data !== null) // null 데이터 제외
                .sort((a, b) => b.volume - a.volume); // 거래량(volume) 기준으로 내림차순 정렬

            setFilteredData(formattedData);

        }
    }, [stockList, stockData]);

    const handleSelectChange = (selectedOption) => {
        setSelectedStock(selectedOption);
    };

    const handleDataFetch = (data) => {
        setStockData(data);
    };

    const customStyles = {
        option: (provided) => ({
            ...provided,
            whiteSpace: 'nowrap',
            fontSize: '14px',
            overflow: 'hidden',
            textOverflow: 'ellipsis',
            height: '2.1em',
        }),
        container: (provided) => ({
            ...provided,
            width: '450px'
        })
    };

    return (
        <>
            <Navbar/>
            <div className='home-container'>
                <FetchStockList onListFetch={setStockList}/>
                <FetchStockDataForCode stockCode={data.stock_code} onSDFCFetch={handleDataFetch}/>

                <FetchRealTime onDataFetch={setStockData}/>
                <div className="home-search-bar">
                    <Select
                        components={{MenuList}}
                        options={availableStocks}
                        onChange={handleSelectChange}
                        value={selectedStock}
                        placeholder="종목을 검색하세요"
                        styles={customStyles}
                        inputValue={inputValue}
                        onInputChange={(value) => setInputValue(value)}
                    />
                </div>

                <div className='home-table-container'>
                    <table className="table">
                        <thead>
                        <tr>
                            <th>종목명</th>
                            <th>현재가</th>
                            <th>전일대비</th>
                            <th>등락율</th>
                            <th>고가</th>
                            <th>저가</th>
                            <th>시가</th>
                            <th>거래량</th>
                        </tr>
                        </thead>
                        <tbody>
                        {filteredData.map((data, index) => (
                            <React.Fragment key={index}>
                                {/* 종목 데이터를 표시하는 기본 행 */}
                                <tr>
                                    <td>
                                        <button
                                            className="stock-name-button"
                                            onClick={() => handleStockClick(data.stock_code)}
                                        >
                                            {data.stock_name}
                                        </button>
                                    </td>
                                    <td>{data.current_price}원</td>
                                    <td>{data.onday}원</td>
                                    <td
                                        style={{
                                            color: parseFloat(data.ratio) < 0 ? 'red' : 'skyblue'
                                        }}
                                    >
                                        {data.ratio}%
                                    </td>
                                    <td>{data.high_price}원</td>
                                    <td>{data.low_price}원</td>
                                    <td>{data.open_price}원</td>
                                    <td>{data.volume}</td>
                                </tr>

                                {/* 선택된 종목에 해당하는 차트와 버튼을 표시할 행 */}
                                {expandedRows.includes(data.stock_code) && (
                                    <tr>
                                        <td colSpan="8"> {/* 8칸을 차지하도록 설정 */}
                                            <div className="home-chart-container">
                                                <ChartOfClose stockCode={data.stock_code}/>
                                                <div style={{
                                                    display: 'flex',
                                                    justifyContent: 'space-around',
                                                    marginTop: '10px'
                                                }}>
                                                    <button onClick={() => navigate('/comprehensiveanalysis', {
                                                        state: {
                                                            stockCode: data.stock_code,
                                                            stockName: data.stock_name
                                                        }
                                                    })} className="more-button"> 종합 분석
                                                    </button>
                                                    <button onClick={() => navigate('/techChart', {
                                                        state: {
                                                            stockCode: data.stock_code,
                                                            stockName: data.stock_name
                                                        }
                                                    })} className="like-button"> 기술적 차트
                                                    </button>
                                                </div>
                                            </div>
                                        </td>
                                    </tr>
                                )}
                            </React.Fragment>
                        ))}
                        </tbody>
                    </table>
                </div>
            </div>
        </>
    );
}

export default Home;
