import React, { useState, useEffect } from "react";
import FetchRSIData from "./FetchRSIData";
import FetchStockData from "./FetchStockData";
import FetchStockList from "./FetchStockList";
import Highcharts from "highcharts";
import HighchartsReact from "highcharts-react-official";

function ChartOfRSI({ stockCode }) {
    const [stockData, setStockData] = useState([]);
    const [stockList, setStockList] = useState([]);
    const [rsiData, setRsiData] = useState([]);
    const [options, setOptions] = useState({});

    useEffect(() => {
        if (stockList.length > 0 && stockData.length > 0 && rsiData.length > 0) {

            const stockCodeSet = new Set(stockData.map(item => item.stock_code));
            const filteredStockList = stockList.filter(item => stockCodeSet.has(item.code));

            const formattedData = rsiData.map(item => {
                return [
                    new Date(item.date).getTime(),
                    item.RSI
                ];
            });


            setOptions({
                chart: {
                    type: 'line'
                },
                title: {
                    text: ``
                },
                xAxis: {
                    type: 'datetime',
                    title: {
                        text: ''
                    }
                },
                yAxis: {
                    title: {
                        text: ''
                    },
                    min: 0, // Y축 최소값 0으로 고정
                    max: 100, // Y축 최대값 100으로 고정
                    plotBands: [{
                        from: 0,
                        to: 30,
                        color: 'rgba(54, 162, 235, 0.2)' // 30 이하 RSI 영역 반투명 색상
                    }, {
                        from: 70,
                        to: 100,
                        color: 'rgba(54, 162, 235, 0.2)' // 70 이상 RSI 영역 반투명 색상
                    }]
                },
                series: [{
                    name: 'RSI',
                    marker: {
                        enabled: false
                    },
                    data: formattedData
                }]
            });
        }
    }, [stockList, stockData, rsiData, stockCode]);

    return (
        <div>
            <FetchStockData onDataFetch={setStockData} />
            <FetchStockList onListFetch={setStockList} />
            <FetchRSIData stockCode={stockCode} onRSIFetch={setRsiData} />

            {options.series && options.series.length > 0 && (
                <HighchartsReact highcharts={Highcharts} options={options} />
            )}
        </div>
    );
}

export default ChartOfRSI;
