import React, { useState, useEffect } from "react";
import FetchRSIData from "./FetchRSIData";
import FetchStockDataForCode from "./FetchStockDataForCode";
import FetchStockList from "./FetchStockList";
import Highcharts from "highcharts";
import HighchartsReact from "highcharts-react-official";

function ChartOfRSI({ stockCode }) {
    const [stockData, setStockData] = useState([]);
    const [stockList, setStockList] = useState([]);
    const [rsiData, setRsiData] = useState([]);
    const [options, setOptions] = useState({});

    useEffect(() => {
        if (stockList.length > 0 && stockData.length > 0 && rsiData.length > 0) {

            const formattedData = rsiData.map(item => {
                return [
                    new Date(item.date).getTime(),
                    item.RSI
                ];
            });


            setOptions({
                chart: {
                    type: 'line',
                    height: 220,
                },
                title: {
                    text: ''
                },
                accessibility: {
                    enabled: false
                },
                xAxis: {
                    type: 'datetime',
                    labels: {
                        formatter: function () {
                            return Highcharts.dateFormat('%m. %d', this.value);
                        }
                    },
                },
                yAxis: {
                    title: {
                        text: ''
                    },
                    min: 20,
                    max: 80,
                    tickPositions: [0, 30, 70],
                    plotBands: [{
                        from: 30,
                        to: 70,
                        color: 'rgba(221, 160, 221, 0.2)' // RSI 범위 표시 (30~70 사이 반투명 영역)
                    }],
                     plotLines: [
                        {
                            value: 70, // 70선 설정
                            color: 'gray',
                            dashStyle: 'dash', // 점선 스타일
                            width: 1,
                            zIndex: 5,
                        },
                        {
                            value: 30, // 30선 설정
                            color: 'gray',
                            dashStyle: 'dash', // 점선 스타일
                            width: 1,
                            zIndex: 5,
                        }
                    ],
                    gridLineColor: '#e6e6e6',  // 그리드 라인의 색상
                },
                series: [{
                    name: 'RSI',
                    marker: {
                        enabled: false
                    },
                    lineWidth: 1,  // 선 굵기 설정
                    color: '#8E44AD',  // 선 색상 설정 (보라색 계열)
                    data: formattedData,
                    tooltip: {
                        valueDecimals: 2 // 소수점 2자리로 표시
                    }
                }],
                legend: {
                    enabled: false // 범례 숨기기
                },
                credits: {
                    enabled: false // "Highcharts.com" 표시 비활성화
                },
                tooltip: {
                    shared: true,
                    crosshairs: true
                },
            });
        }
    }, [stockList, stockData, rsiData, stockCode]);

    return (
        <div>
            <FetchStockDataForCode stockCode={stockCode} onSDFCFetch={setStockData} />
            <FetchStockList onListFetch={setStockList} />
            <FetchRSIData stockCode={stockCode} onRSIFetch={setRsiData} />

            {options.series && options.series.length > 0 && (
                <HighchartsReact highcharts={Highcharts} options={options} />
            )}
        </div>
    );
}

export default ChartOfRSI;
