import React, { useState, useEffect } from 'react';
import FetchADXData from '../ComponentsFetch/FetchADXData';

function ADXAnalysis3({ stockCode }) {
    const [adxData, setADXData] = useState([]);
    const [analysisResult, setAnalysisResult] = useState('');

    const analyzeADXDirection = (adxData) => {
        if (adxData.length < 2) {
            return '데이터가 충분하지 않습니다.';
        }

        const lastIndex = adxData.length - 1;
        const currentADX = adxData[lastIndex].ADX;
        const prevADX = adxData[lastIndex - 1].ADX;

        let result = '<strong>ADX의 방향성 분석</strong><br/><br/>';

        if (currentADX > prevADX) {
            result += 'ADX가 상승하고 있어 추세의 강도가 <strong>강화</strong>되고 있습니다.<br/>';
        } else if (currentADX < prevADX) {
            result += 'ADX가 하락하고 있어 추세의 강도가 <strong>약화</strong>되고 있습니다.<br/>';
        } else {
            result += 'ADX에 큰 변화가 없어 추세의 강도가 유지되고 있습니다.<br/>';
        }

        return result;
    };

    useEffect(() => {
        if (adxData.length > 0) {
            const result = analyzeADXDirection(adxData);
            setAnalysisResult(result);
        }
    }, [adxData]);

    return (
        <div>
            <FetchADXData stockCode={stockCode} onADXFetch={setADXData} />
            <h4>ADX의 방향성 분석</h4>
            <p dangerouslySetInnerHTML={{ __html: analysisResult }}></p>
        </div>
    );
}

export default ADXAnalysis3;
