import React, { useEffect, useState } from "react";

function findSupportZones(stockData) {
    let lows = [];
    let firstLow = null;

    for (let i = 1; i < stockData.length - 1; i++) {
        // Compare previous, current, and next close prices to find a low
        if (stockData[i - 1].close > stockData[i].close && stockData[i].close < stockData[i + 1].close) {
            if (firstLow === null) {
                firstLow = { date: stockData[i].date, close: stockData[i].close };
            } else {
                if (stockData[i].close < firstLow.close) {
                    lows.push(firstLow);
                    firstLow = { date: stockData[i].date, close: stockData[i].close };
                } else {
                    lows.push({ date: stockData[i].date, close: stockData[i].close });
                }
            }
        }
    }

    // Add the last identified low if it exists
    if (firstLow) {
        lows.push(firstLow);
    }

    return lows;
}

function StockAnalysis({ stockData }) {
    const [supportZones, setSupportZones] = useState([]);

    useEffect(() => {
        if (stockData.length > 0) {
            const zones = findSupportZones(stockData);
            setSupportZones(zones);
        }
    }, [stockData]);

    return (
        <div>
            <h3>Support Zones</h3>
            <ul>
                {supportZones.map((zone, index) => (
                    <li key={index}>
                        Date: {zone.date}, Close: {zone.close}
                    </li>
                ))}
            </ul>
        </div>
    );
}

export default StockAnalysis;
