import React, { useState, useEffect } from "react";
import Highcharts from "highcharts";
import HighchartsReact from "highcharts-react-official";
import FetchStockDataForCode from "./FetchStockDataForCode";
import FetchParabolicSARData from "./FetchParabolicSARData";

function ChartOfSAR({ stockCode }) {
    const [stockData, setStockData] = useState([]);
    const [stockSAR, setStockSAR] = useState([]);
    const [options, setOptions] = useState({});

    useEffect(() => {
        // 데이터가 제대로 로드되었는지 확인
        if (stockData.length > 0 && stockSAR.length > 0 && stockCode) {
            // JSON 데이터에 따른 날짜와 종가(Close) 매핑
            const dataForChart = stockData
                .map(item => ({
                    date: new Date(item.date).toLocaleDateString('ko-KR', { month: '2-digit', day: '2-digit' }),
                    close: item.close, // 종가(Close) 값
                }))
                .sort((a, b) => new Date(a.date) - new Date(b.date));

            // Parabolic SAR 데이터 매핑
            const sarForChart = stockSAR
                .map(item => ({
                    date: new Date(item.date).toLocaleDateString('ko-KR', { month: '2-digit', day: '2-digit' }),
                    sar: item.Parabolic_SAR, // Parabolic SAR 값
                }))
                .sort((a, b) => new Date(a.date) - new Date(b.date));

            // Set chart options
            setOptions({
                chart: {
                    type: 'line',
                    height: 330,
                },
                title: {
                    text: '',
                },
                xAxis: {
                    categories: dataForChart.map(item => item.date),
                    labels: {
                        formatter: function () {
                            return this.value;
                        },
                    },
                },
                yAxis: {
                    title: {
                        text: '',
                    },
                },
                series: [
                    {
                        name: 'Close Price',
                        data: dataForChart.map(item => item.close),
                        marker: {
                            enabled: false, // Disable data point markers for close price
                        },
                        color: '#0071e3',
                        lineWidth: 1,
                    },
                    {
                        name: 'Parabolic SAR',
                        type: 'scatter', // Scatter plot for SAR
                        data: sarForChart.map(item => item.sar),
                        marker: {
                            symbol: 'plus',
                            radius: 1, // 점 크기 설정
                        },
                        color: '#4B4B4B', // 점 색상 설정 (Parabolic SAR 색상)
                        tooltip: {
                            pointFormat: 'Parabolic SAR: <b>{point.y}</b>',
                        },
                    },
                ],
                legend: {
                    enabled: true, // Show legend to distinguish between Close Price and Parabolic SAR
                },
                credits: {
                    enabled: false, // Disable the "Highcharts.com" text
                },
                tooltip: {
                    shared: true,
                    crosshairs: true,
                },
                navigator: {
                    enabled: true, // Enable the scrollable range navigator (adds a zooming feature)
                },
                scrollbar: {
                    enabled: true, // Enable the scrollbar for manual zooming
                },
            });
        }
    }, [stockData, stockSAR, stockCode]);

    // 데이터가 없을 때는 로딩 메시지 혹은 빈 상태를 보여줌
    if (!stockCode || stockData.length === 0 || stockSAR.length === 0) return <div>데이터를 로드 중입니다...</div>;

    return (
        <div>
            <FetchStockDataForCode stockCode={stockCode} onSDFCFetch={setStockData} />
            <FetchParabolicSARData stockCode={stockCode} onSARFetch={setStockSAR} />
            <HighchartsReact highcharts={Highcharts} options={options} />
        </div>
    );
}

export default ChartOfSAR;
