import { useState, useEffect } from "react";

function FetchRealTime({ onDataFetch }) {
    const [stockData, setStockData] = useState([]);

    useEffect(() => {
        // 데이터를 불러오는 함수 정의
        const fetchData = () => {
            fetch('http://127.0.0.1:8000/api/get_real_time_data/')
                .then(response => response.json())
                .then(data => {
                    setStockData(data); // 받아온 데이터를 상태로 저장
                    if (onDataFetch) {
                        onDataFetch(data); // 외부로 데이터 전달
                    }
                })
                .catch(error => console.error('Error fetching stock data:', error));
        };

        // 페이지 로드 시 데이터 최초 1회 호출
        fetchData();

        // 주기적으로 데이터를 갱신 (5초마다 호출)
        const intervalId = setInterval(fetchData, 5000);

        // 컴포넌트 언마운트 시 인터벌 클리어
        return () => clearInterval(intervalId);
    }, [onDataFetch]); // onDataFetch가 변경되면 재실행

    return (
        <div>
            <h1>Real-Time Stock Data</h1>
            <table border="1" cellPadding="5" cellSpacing="0">
                <thead>
                <tr>
                    <th>종목 코드</th>
                    <th>현재가</th>
                    <th>전일대비</th>
                    <th>등락율</th>
                    <th>고가</th>
                    <th>저가</th>
                    <th>시가</th>
                    <th>거래량</th>
                </tr>
                </thead>
                <tbody>
                {Object.keys(stockData).map(stockCode => (
                    <tr key={stockCode}>
                        <td>{stockCode}</td>
                        <td>{stockData[stockCode].current_price}원</td>
                        <td>{stockData[stockCode].onday}원</td>
                        <td>{stockData[stockCode].ratio}%</td>
                        <td>{stockData[stockCode].high_price}원</td>
                        <td>{stockData[stockCode].low_price}원</td>
                        <td>{stockData[stockCode].open_price}원</td>
                        <td>{stockData[stockCode].volume}</td>
                    </tr>
                ))}
                </tbody>
            </table>
        </div>
    );
}

export default FetchRealTime;
