import React, { useState, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import FetchLatestStockData from "../ComponentsFetch/FetchStockOrignal/FetchLatestStockData";
import FetchStockList from "../ComponentsFetch/FetchStockOrignal/FetchStockList";
import FetchTop5Data from "../ComponentsFetch/FetchStockOrignal/FetchTop5Data";
import ChartOfClose from "../ComponentsChart/ChartOfClose";
import Navbar from "../components/Navbar";
import './Home.css';

function Home() {
    const [stockData, setStockData] = useState([]);
    const [stockList, setStockList] = useState([]);
    const [filteredData, setFilteredData] = useState([]);
    const [selectedStock, setSelectedStock] = useState(null);
    const [topStocks, setTopStocks] = useState([]);  // 상위 5개 종목 상태 추가
    const [selectedStockName, setSelectedStockName] = useState("");
    const navigate = useNavigate();
    const [isMenuOpen, setIsMenuOpen] = useState(false);
    const menuRef = useRef(null);

    useEffect(() => {
        const outSideClick = (e) => {
            const { target } = e;
            if (isMenuOpen && menuRef.current && !menuRef.current.contains(target)) {
                setIsMenuOpen(false);
            }
        };
        document.body.classList.add('home');
        document.addEventListener("mousedown", outSideClick);
        return () => {
            document.body.classList.remove('home');
            document.removeEventListener("mousedown", outSideClick);
        };
    }, [isMenuOpen]);

    useEffect(() => {
        console.log(stockList.length, stockData.length)
        if (stockList.length > 0 && stockData.length > 0) {
            const formattedData = stockData
                .map(data => {
                    const matchingStock = stockList.find(stock => stock.code === data.stock_code);
                    if (matchingStock) {
                        const date = new Date(data.date);
                        const formattedDate = `${(date.getMonth() + 1).toString().padStart(2, '0')}.${date.getDate().toString().padStart(2, '0')}`;
                        return {
                            ...data,
                            stock_name: matchingStock.name,
                            date: formattedDate
                        };
                    }
                    return null;
                })
                .filter(data => data !== null)
                .sort((a, b) => b.volume - a.volume); // 거래량(volume) 기준으로 내림차순 정렬

            setFilteredData(formattedData);
        }
    }, [stockList, stockData]);

    useEffect(() => {
        console.log('filteredData: ', filteredData);
    }, [filteredData]);

    useEffect(() => {
        console.log('stockData: ', stockData);
    }, [stockData]);

    const handleStockClick = (stockCode, stockName) => {
        setSelectedStock(stockCode);
        setSelectedStockName(stockName); // 클릭한 종목 이름을 저장
    };

    return (
        <>
            <Navbar />

            <div className='home-container'>
                <FetchStockList onListFetch={setStockList}/>
                <FetchLatestStockData onLSDFetch={setStockData}/>
                
                <div className='table-container'>
                    <table className="table">
                        <thead>
                        <tr>
                            <th>종목명</th>
                            <th>일자</th>
                            <th>종가</th>
                            <th>고가</th>
                            <th>저가</th>
                            <th>시가</th>
                            <th>거래량</th>
                        </tr>
                        </thead>
                        <tbody>
                        {filteredData.map((data, index) => (
                            <tr key={index}>
                                <td
                                    className="stock_name"
                                    onClick={() => handleStockClick(data.stock_code, data.stock_name)}
                                    style={{
                                        cursor: 'pointer',
                                        color: 'blue',
                                    }}
                                >
                                    {data.stock_name}
                                </td>
                                <td>{data.date}</td>
                                <td>{data.close}</td>
                                <td>{data.high}</td>
                                <td>{data.low}</td>
                                <td>{data.open}</td>
                                <td>{data.volume}</td>
                            </tr>
                        ))}
                        </tbody>
                    </table>
                </div>
                <div className="chart-container">
                    {selectedStock && (
                        <>
                            <h3 className="chart-container-text">{selectedStockName}</h3>
                            <ChartOfClose stockCode={selectedStock}/>
                            <button onClick={() => navigate('/detail', {
                                state: {
                                    stockCode: selectedStock,
                                    stockName: selectedStockName
                                }
                            })} className="more-button"> 더 보기
                            </button>
                            <button onClick={() => navigate('/techChart', {
                                state: {
                                    stockCode: selectedStock,
                                    stockName: selectedStockName
                                }
                            })} className="like-button"> 기술적 차트
                            </button>
                        </>
                    )}
                </div>
            </div>
        </>
    );
}

export default Home;
