import React, {useState, useEffect} from "react";
import FetchCCIData from "../ComponentsFetch/FetchCCIData";
import FetchStockDataForCode from "../ComponentsFetch/FetchStockDataForCode";
import HighchartsReact from "highcharts-react-official";
import Highcharts from "highcharts";

function ChartOfCCI({ stockCode }) {
    const [stockData, setStockData] = useState([])
    const [CCIData, setCCIData] = useState([])
    const [options, setOptions] = useState({})

    useEffect(() => {
        if(stockData.length > 0 && CCIData.length>0 && stockCode) {

            const formattedCCIData = CCIData.map(item => {
                return [
                    new Date(item.date).getTime(),
                    item.CCI
                ];
            });

            setOptions( {
                chart: {
                    type: 'line',
                    height: 220,
                },
                title: {
                    text: ''
                },
                accessibility: {
                    enabled: false
                },
                xAxis: {
                    type: 'datetime',
                    labels: {
                        formatter: function() {
                            return Highcharts.dateFormat('%m. %d', this.value);
                        }
                    },
                },
                yAxis: {
                    title: {
                        text: '' // Y축 제목
                    },
                    softMin: 50,
                    softMax: 200,
                    tickPositions: [-220, 30, 50, 70, 250],
                    plotBands: [{
                        from: -100,
                        to: 100,
                        color: 'rgba(221, 221, 132, 0.3)' // RSI 범위 표시 (30~70 사이 반투명 영역)
                    }],
                    labels: {
                        formatter: function () {
                            if (this.value === -250 || this.value === 250) {
                                return '';  // 20과 80은 빈 문자열로 숨기기
                            }
                            return this.value;  // 나머지 값은 그대로 표시
                        }
                    },
                    plotLines: [{
                        value: 100,
                        color: '#A9A9A9',
                        dashStyle: 'shortdash',
                        width: 1,
                        label: {
                            text: '' // 과매수 상태
                        }
                    }, {
                        value: -100,
                        color: '#A9A9A9',
                        dashStyle: 'shortdash',
                        width: 1,
                        label: {
                            text: '' // 과매도 상태
                        }
                    }]
                },
                series: [{
                    name: 'CCI',
                    data: formattedCCIData, // CCI 데이터 삽입
                    tooltip: {
                        valueDecimals: 2 // 소수점 자리 설정
                    },
                    color: '#CCCC66',
                    lineWidth: 1.5,
                }]
            });
        }
    }, [stockData, CCIData, stockCode])
    return (
        <div>
            <FetchStockDataForCode stockCode={stockCode} onSDFCFetch={setStockData}/>
            <FetchCCIData stockCode={stockCode} onCCIFetch={setCCIData}/>

            {options.series && options.series.length > 0 && (
                <HighchartsReact highcharts={Highcharts} options={options} />
            )}
        </div>
    );
}
export default ChartOfCCI
