import React, { useEffect, useState } from "react";
import FetchMACDData from "./FetchMACDData";
import FetchStockDataForCode from "./FetchStockDataForCode";

function MACDAnalysis2({ stockCode }) {
    const [macdData, setMACDData] = useState([]);
    const [priceData, setPriceData] = useState([]);
    const [analysisText, setAnalysisText] = useState("");

    useEffect(() => {
        if (macdData.length > 0 && priceData.length > 0) {
            const analysis = performDivergenceAnalysis();
            setAnalysisText(analysis);
        }
    }, [macdData, priceData]);

    const performDivergenceAnalysis = () => {
        const period = 10; // 각 구간을 10일 기준으로 나누기
        const recentDays = 20; // 거래일 기준 20일

        // 최근 20 거래일 기준으로 데이터를 가져옵니다.
        const tradingDays = priceData.slice(-recentDays);

        const firstLow = findLowestPoint(tradingDays.slice(0, period));
        const secondLow = firstLow ? findLowestPoint(tradingDays.slice(period, recentDays)) : null;

        const firstHigh = findHighestPoint(tradingDays.slice(0, period));
        const secondHigh = firstHigh ? findHighestPoint(tradingDays.slice(period, recentDays)) : null;

        let analysis = "";

        if (firstLow && secondLow) {
            const firstMACDLow = macdData[firstLow.index].MACD;
            const secondMACDLow = macdData[secondLow.index].MACD;

            if (firstLow.price > secondLow.price && firstMACDLow < secondMACDLow) {
                analysis += `상승 다이버전스 발생: 첫 번째 저점(${secondLow.date})부터 두 번째 저점(${firstLow.date})까지. 이는 강력한 매수 신호일 수 있습니다.\n`;
            } else if (firstLow.price < secondLow.price && firstMACDLow > secondMACDLow) {
                analysis += `하락 다이버전스 발생: 첫 번째 저점(${secondLow.date})부터 두 번째 저점(${firstLow.date})까지. 이는 강력한 매도 신호일 수 있습니다.\n`;
            }
        }

        if (firstHigh && secondHigh) {
            const firstMACDHigh = macdData[firstHigh.index].MACD;
            const secondMACDHigh = macdData[secondHigh.index].MACD;

            if (firstHigh.price < secondHigh.price && firstMACDHigh > secondMACDHigh) {
                analysis += `약세 다이버전스 발생: 첫 번째 고점(${secondHigh.date})부터 두 번째 고점(${firstHigh.date})까지. 이는 매도 신호일 수 있습니다.\n`;
            } else if (firstHigh.price > secondHigh.price && firstMACDHigh < secondMACDHigh) {
                analysis += `강세 다이버전스 발생: 첫 번째 고점(${secondHigh.date})부터 두 번째 고점(${firstHigh.date})까지. 이는 매수 신호일 수 있습니다.\n`;
            }
        }

        if (!analysis) {
            return "최근 20 거래일 내에 유의미한 다이버전스가 발생하지 않았습니다.";
        }

        return analysis;
    };

    const findLowestPoint = (data) => {
        let lowest = { price: Infinity, index: -1, date: "" };

        for (let i = 2; i < data.length; i++) {
            const a = data[i - 2];
            const b = data[i - 1];
            const c = data[i];

            // a > b < c 조건을 확인하고 가장 낮은 종가를 선택
            if (a.close > b.close && b.close < c.close) {
                if (b.close < lowest.price) {
                    lowest = { price: b.close, index: i - 1, date: b.date };
                }
            }
        }

        return lowest.index !== -1 ? lowest : null;
    };

    const findHighestPoint = (data) => {
        let highest = { price: -Infinity, index: -1, date: "" };

        for (let i = 2; i < data.length; i++) {
            const a = data[i - 2];
            const b = data[i - 1];
            const c = data[i];

            // a < b > c 조건을 확인하고 가장 높은 종가를 선택
            if (a.close < b.close && b.close > c.close) {
                if (b.close > highest.price) {
                    highest = { price: b.close, index: i - 1, date: b.date };
                }
            }
        }

        return highest.index !== -1 ? highest : null;
    };

    return (
        <div>
            <FetchMACDData stockCode={stockCode} onMACDFetch={setMACDData} />
            <FetchStockDataForCode stockCode={stockCode} onSDFCFetch={setPriceData} />
            <h4 className="analysis-text">Divergence 분석</h4>
            <p>{analysisText}</p>
        </div>
    );
}

export default MACDAnalysis2;
