import React, { useEffect, useState } from "react";
import FetchMACDData from "./FetchMACDData";
import FetchStockDataForCode from "./FetchStockDataForCode";

function MACDAnalysis2({ stockCode }) {
    const [macdData, setMACDData] = useState([]);
    const [priceData, setPriceData] = useState([]);
    const [analysisText, setAnalysisText] = useState("");

    useEffect(() => {
        if (macdData.length > 0 && priceData.length > 0) {
            const analysis = performDivergenceAnalysis();
            setAnalysisText(analysis);
        }
    }, [macdData, priceData]);

    const performDivergenceAnalysis = () => {
        const period = 14; // 14일 기준

        const periods = splitDataIntoPeriods(priceData, period);
        let divergenceText = "";

        for (let i = 0; i < periods.length - 1; i++) {
            const firstPeriod = periods[i];
            const secondPeriod = periods[i + 1];

            const firstLow = findLowPoint(firstPeriod);
            const secondLow = findLowPoint(secondPeriod);
            const firstHigh = findHighPoint(firstPeriod);
            const secondHigh = findHighPoint(secondPeriod);

            if (firstLow && secondLow) {
                const firstMACDLow = macdData[firstLow.index].MACD;
                const secondMACDLow = macdData[secondLow.index].MACD;

                if (firstLow.price > secondLow.price && firstMACDLow < secondMACDLow) {
                    divergenceText += `<strong>상승 다이버전스 발생</strong>: 첫 번째 저점(${secondLow.date})부터 두 번째 저점(${firstLow.date})까지. 이는 강력한 매수 신호일 수 있습니다.<br /><br />`;
                } else if (firstLow.price < secondLow.price && firstMACDLow > secondMACDLow) {
                    divergenceText += `<strong>하락 다이버전스 발생</strong>: 첫 번째 저점(${secondLow.date})부터 두 번째 저점(${firstLow.date})까지. 이는 강력한 매도 신호일 수 있습니다.<br /><br />`;
                }
            }

            if (firstHigh && secondHigh) {
                const firstMACDHigh = macdData[firstHigh.index].MACD;
                const secondMACDHigh = macdData[secondHigh.index].MACD;

                if (firstHigh.price < secondHigh.price && firstMACDHigh > secondMACDHigh) {
                    divergenceText += `<strong>하락 다이버전스 발생</strong>: 첫 번째 고점(${secondHigh.date})부터 두 번째 고점(${firstHigh.date})까지. 이는 강력한 매도 신호일 수 있습니다.<br /><br />`;
                } else if (firstHigh.price > secondHigh.price && firstMACDHigh < secondMACDHigh) {
                    divergenceText += `<strong>상승 다이버전스 발생</strong>: 첫 번째 고점(${secondHigh.date})부터 두 번째 고점(${firstHigh.date})까지. 이는 강력한 매수 신호일 수 있습니다.<br /><br />`;
                }
            }
        }

        return divergenceText || "다이버전스가 감지되지 않았습니다.";
    };

    const splitDataIntoPeriods = (data, periodLength) => {
        const periods = [];
        for (let i = data.length - 1; i >= 0; i -= periodLength) {
            periods.push(data.slice(Math.max(i - periodLength + 1, 0), i + 1));
        }
        return periods;
    };

    const findLowPoint = (data) => {
        let lowest = { price: Infinity, index: -1, date: "" };
        data.forEach((point, index) => {
            if (point.close < lowest.price) {
                lowest = { price: point.close, index, date: point.date };
            }
        });
        return lowest.index !== -1 ? lowest : null;
    };

    const findHighPoint = (data) => {
        let highest = { price: -Infinity, index: -1, date: "" };
        data.forEach((point, index) => {
            if (point.close > highest.price) {
                highest = { price: point.close, index, date: point.date };
            }
        });
        return highest.index !== -1 ? highest : null;
    };

    return (
        <div>
            <FetchMACDData stockCode={stockCode} onMACDFetch={setMACDData} />
            <FetchStockDataForCode stockCode={stockCode} onSDFCFetch={setPriceData} />
            <h4 className="analysis-text">Divergence 분석</h4>
            <p dangerouslySetInnerHTML={{ __html: analysisText }} />
        </div>
    );
}

export default MACDAnalysis2;
