import React, { useState, useEffect } from "react";
import FetchMFIData from "../ComponentsFetch/FetchMFIData";
import FetchStockDataForCode from "../ComponentsFetch/FetchStockDataForCode";
import HighchartsReact from "highcharts-react-official";
import Highcharts from "highcharts";
import HighchartsStock from "highcharts/modules/stock"; // Highcharts Stock 모듈 추가

// Highcharts 모듈 활성화
HighchartsStock(Highcharts);

function ChartOfMFI({ stockCode }) {
    const [stockData, setStockData] = useState([]);
    const [MFIData, setMFIData] = useState([]);
    const [options, setOptions] = useState({});
    const [isDragging, setIsDragging] = useState(false);
    const [dragStart, setDragStart] = useState(null);
    const [startRange, setStartRange] = useState(null);

    useEffect(() => {
        if (stockData.length > 0 && MFIData.length > 0 && stockCode) {
            // MFI 데이터 포맷팅
            const formattedMFIData = MFIData.map(item => [new Date(item.date).getTime(), item.MFI]);
            const formattedVolumeData = stockData.map(item => ({ x: new Date(item.date).getTime(), y: item.volume }));
            const formattedClosePriceData = stockData.map(item => ({ x: new Date(item.date).getTime(), y: item.close }));

            setOptions({
                chart: {
                    height: 600, // 차트 전체 높이 설정
                    zoomType: 'x', // 확대 비활성화
                    events: {
                        load() {
                            const chart = this;

                            // 마우스 다운 이벤트 (드래그 시작)
                            Highcharts.addEvent(chart.container, 'mousedown', function (e) {
                                e = chart.pointer.normalize(e);
                                setIsDragging(true);
                                setDragStart(e.chartX);
                                setStartRange(chart.xAxis[0].min);
                            });

                            // 마우스 이동 이벤트 (차트 이동)
                            Highcharts.addEvent(chart.container, 'mousemove', function (e) {
                                if (isDragging) {
                                    e = chart.pointer.normalize(e);
                                    const dragDiff = dragStart - e.chartX;
                                    const range = chart.xAxis[0].max - chart.xAxis[0].min;
                                    const newMin = startRange + dragDiff * range / chart.plotWidth;
                                    const newMax = newMin + range;

                                    chart.xAxis[0].setExtremes(newMin, newMax, true, false);
                                }
                            });

                            // 마우스 업 이벤트 (드래그 종료)
                            Highcharts.addEvent(chart.container, 'mouseup', function () {
                                setIsDragging(false);
                            });

                            // 마우스 나가기 이벤트 (드래그 종료)
                            Highcharts.addEvent(chart.container, 'mouseleave', function () {
                                setIsDragging(false);
                            });
                        }
                    }
                },
                title: {
                    text: 'Close Price, MFI, and Volume Chart' // 차트 제목
                },
                accessibility: {
                    enabled: false // 접근성 비활성화
                },
                xAxis: {
                    type: 'datetime', // x축은 날짜 형식
                    crosshair: true, // 마우스 호버 시 십자선 표시
                },
                yAxis: [
                    {
                        title: {
                            text: 'Close Price' // Close Price용 y축
                        },
                        height: '50%', // Close Price 차트는 상단 50% 영역에 위치
                        lineWidth: 2,
                        opposite: false, // 왼쪽에 위치
                    },
                    {
                        title: {
                            text: 'MFI' // MFI용 y축
                        },
                        top: '52%', // MFI 차트는 상단 52%부터 시작
                        height: '28%', // MFI 차트 높이는 28%
                        offset: 0,
                        lineWidth: 2,
                        min: 0,
                        max: 100,
                        tickPositions: [5, 20, 80, 95],
                        plotBands: [{
                            from: 20,
                            to: 80,
                            color: 'rgba(144, 238, 144, 0.1)' // MFI의 20~80 구간에 반투명 색상 추가
                        }],
                        labels: {
                            formatter: function () {
                                if (this.value === 5 || this.value === 95) {
                                    return '';
                                }
                                return this.value;
                            }
                        },
                        plotLines: [{
                            value: 80,
                            color: 'gray',
                            dashStyle: 'dash',
                            width: 1,
                            zIndex: 5,
                        },
                        {
                            value: 20,
                            color: 'gray',
                            dashStyle: 'dash',
                            width: 1,
                            zIndex: 5,
                        }],
                        opposite: false, // 왼쪽에 위치
                    },
                    {
                        title: {
                            text: 'Volume', // Volume용 y축
                        },
                        top: '82%', // Volume 차트는 상단 82%부터 시작
                        height: '18%', // Volume 차트는 하단 18%에 위치
                        offset: 0,
                        lineWidth: 2,
                        labels: {
                            enabled: false // 볼륨에 라벨 비활성화
                        },
                        opposite: false, // 왼쪽에 위치
                    }
                ],
                series: [
                    {
                        name: 'Close Price',
                        type: 'line',
                        data: formattedClosePriceData, // Close Price 데이터
                        marker: {
                            enabled: false,
                        },
                        yAxis: 0, // 첫 번째 yAxis (Close Price)
                        color: '#0071e3', // Close Price 라인 색상
                        lineWidth: 1.5,
                    },
                    {
                        name: 'MFI',
                        type: 'line',
                        data: formattedMFIData, // MFI 데이터
                        marker: {
                            enabled: false,
                        },
                        yAxis: 1, // 두 번째 yAxis (MFI)
                        color: '#66CC75', // MFI 라인 색상
                        lineWidth: 1.5,
                    },
                    {
                        name: 'Volume',
                        type: 'column',
                        data: formattedVolumeData, // Volume 데이터
                        yAxis: 2, // 세 번째 yAxis (Volume)
                        color: '#A9A9A9', // Volume 컬럼 색상
                        pointWidth: 1,
                    }
                ],
                legend: {
                    enabled: true // 범례 활성화
                },
                credits: {
                    enabled: false // "Highcharts.com" 텍스트 비활성화
                },
                tooltip: {
                    shared: true, // 툴팁 공유
                    crosshairs: true, // 십자선 표시
                    formatter: function () {
                        let tooltipHtml = `<b>${Highcharts.dateFormat('%m-%d', this.x)}</b><br/>`;
                        this.points.forEach(point => {
                            tooltipHtml += `${point.series.name}: <b>${point.y.toFixed(2)}</b><br/>`;
                        });
                        return tooltipHtml;
                    }
                },
            });
        }
    }, [stockData, MFIData, stockCode]);

    return (
        <div>
            <FetchStockDataForCode stockCode={stockCode} onSDFCFetch={setStockData} />
            <FetchMFIData stockCode={stockCode} onMFIFetch={setMFIData} />
            {options.series && options.series.length > 0 && (
                <HighchartsReact highcharts={Highcharts} options={options} />
            )}
        </div>
    );
}

export default ChartOfMFI;
