import React, { useState, useEffect } from "react";
import FetchMFIData from "../ComponentsFetch/FetchMFIData";
import FetchStockDataForCode from "../ComponentsFetch/FetchStockDataForCode";
import HighchartsReact from "highcharts-react-official";
import Highcharts from "highcharts";

function ChartOfMFI({ stockCode }) {
    const [stockData, setStockData] = useState([]);
    const [MFIData, setMFIData] = useState([]);
    const [options, setOptions] = useState({});

    useEffect(() => {
        if (stockData.length > 0 && MFIData.length > 0 && stockCode) {

            const formattedMFIData = MFIData.map(item => {
                return [
                    new Date(item.date).getTime(),
                    item.MFI
                ];
            });

            const formattedClosePriceData = stockData.map(item => {
                return {
                    x: new Date(item.date).getTime(),
                    y: item.close,
                };
            });

            setOptions({
                chart: {
                    zoomType: 'x', // Zoom 가능
                },
                title: {
                    text: 'MFI and Close Price'
                },
                accessibility: {
                    enabled: false
                },
                xAxis: {
                    type: 'datetime',
                    labels: {
                        formatter: function () {
                            return Highcharts.dateFormat('%m. %d', this.value);
                        }
                    },
                    crosshair: true
                },
                yAxis: [
                    {
                        title: {
                            text: 'MFI'
                        },
                        min: 0,
                        max: 100,
                        tickPositions: [20, 50, 80],
                        plotBands: [{
                            from: 20,
                            to: 80,
                            color: 'rgba(144, 238, 144, 0.1)' // 20에서 80까지 영역 반투명 색상 추가
                        }],
                        labels: {
                            formatter: function () {
                                return this.value;
                            }
                        },
                        plotLines: [
                            {
                                value: 80, // 80선 설정
                                color: 'gray',
                                dashStyle: 'dash', // 점선 스타일
                                width: 1,
                                zIndex: 5,
                            },
                            {
                                value: 20, // 20선 설정
                                color: 'gray',
                                dashStyle: 'dash', // 점선 스타일
                                width: 1,
                                zIndex: 5,
                            }
                        ],
                        opposite: false, // MFI를 왼쪽 y축에 표시
                    },
                    {
                        title: {
                            text: 'Close Price',
                        },
                        labels: {
                            formatter: function () {
                                return this.value;
                            }
                        },
                        opposite: true, // Close Price를 오른쪽 y축에 표시
                    }
                ],
                series: [
                    {
                        name: 'MFI',
                        type: 'line',
                        data: formattedMFIData,
                        yAxis: 0,
                        color: '#66CC75', // 라인 색상 설정
                        lineWidth: 1.5,
                        tooltip: {
                            valueSuffix: ' MFI' // 툴팁에 단위 추가
                        }
                    },
                    {
                        name: 'Close Price',
                        type: 'line',
                        data: formattedClosePriceData,
                        yAxis: 1,
                        color: '#0071e3', // Close Price 색상 설정
                        lineWidth: 1.5,
                        tooltip: {
                            valueSuffix: ' units' // 툴팁에 단위 추가
                        }
                    }
                ],
                legend: {
                    enabled: true // MFI와 Close Price 구분을 위한 범례 활성화
                },
                credits: {
                    enabled: false // "Highcharts.com" 텍스트 비활성화
                },
                tooltip: {
                    shared: true,
                    crosshairs: true,
                    formatter: function () {
                        if (this.points) {
                            let tooltipHtml = `<b>${Highcharts.dateFormat('%m-%d', this.x)}</b><br/>`;
                            this.points.forEach(point => {
                                tooltipHtml += `${point.series.name}: <b>${point.y.toFixed(2)}</b><br/>`;
                            });
                            return tooltipHtml;
                        }
                    }
                },
            });
        }
    }, [stockData, MFIData, stockCode]);

    return (
        <div>
            <FetchStockDataForCode stockCode={stockCode} onSDFCFetch={setStockData} />
            <FetchMFIData stockCode={stockCode} onMFIFetch={setMFIData} />

            {options.series && options.series.length > 0 && (
                <HighchartsReact highcharts={Highcharts} options={options} />
            )}
        </div>
    );
}

export default ChartOfMFI;
