import React, { useEffect, useState } from "react";
import FetchMACDData from "./FetchMACDData";
import FetchStockDataForCode from "./FetchStockDataForCode";

function MACDAnalysis2({ stockCode }) {
    const [macdData, setMACDData] = useState([]);
    const [priceData, setPriceData] = useState([]);
    const [analysisText, setAnalysisText] = useState("");

    useEffect(() => {
        if (macdData.length > 0 && priceData.length > 0) {
            const analysis = performDivergenceAnalysis();
            setAnalysisText(analysis);
        }
    }, [macdData, priceData]);

    const performDivergenceAnalysis = () => {
        const period = 14; // 14일 기준
        const recentDays = 28; // 최근 28일 기준
        const startIndex = priceData.length - 1;

        const firstLow = findLowPoint(priceData, period, startIndex);
        const secondLow = firstLow ? findLowPoint(priceData, period, firstLow.index - 1) : null;

        const firstHigh = findHighPoint(priceData, period, startIndex);
        const secondHigh = firstHigh ? findHighPoint(priceData, period, firstHigh.index - 1) : null;

        let divergenceText = "";

        if (firstLow && secondLow && startIndex - firstLow.index <= recentDays) {
            const firstMACDLow = macdData[firstLow.index].MACD;
            const secondMACDLow = macdData[secondLow.index].MACD;

            if (firstLow.price > secondLow.price && firstMACDLow < secondMACDLow) {
                divergenceText += `상승 다이버전스 발생: 첫 번째 저점(${secondLow.date})부터 두 번째 저점(${firstLow.date})까지. 이는 강력한 매수 신호일 수 있습니다.<br /><br />`;
            } else if (firstLow.price < secondLow.price && firstMACDLow > secondMACDLow) {
                divergenceText += `하락 다이버전스 발생: 첫 번째 저점(${secondLow.date})부터 두 번째 저점(${firstLow.date})까지. 이는 강력한 매도 신호일 수 있습니다.<br /><br />`;
            }
        }

        if (firstHigh && secondHigh && startIndex - firstHigh.index <= recentDays) {
            const firstMACDHigh = macdData[firstHigh.index].MACD;
            const secondMACDHigh = macdData[secondHigh.index].MACD;

            if (firstHigh.price < secondHigh.price && firstMACDHigh > secondMACDHigh) {
                divergenceText += `하락 다이버전스 발생: 첫 번째 고점(${secondHigh.date})부터 두 번째 고점(${firstHigh.date})까지. 이는 강력한 매도 신호일 수 있습니다.<br /><br />`;
            } else if (firstHigh.price > secondHigh.price && firstMACDHigh < secondMACDHigh) {
                divergenceText += `상승 다이버전스 발생: 첫 번째 고점(${secondHigh.date})부터 두 번째 고점(${firstHigh.date})까지. 이는 강력한 매수 신호일 수 있습니다.<br /><br />`;
            }
        }

        return divergenceText || "최근 28일 내에 다이버전스가 발생하지 않았습니다.";
    };

    const findLowPoint = (data, period, startIndex) => {
        let lowest = { price: Infinity, index: -1, date: "" };

        for (let i = startIndex; i >= 2; i--) {
            if (i < period) break;

            const a = data[i - 2];
            const b = data[i - 1];
            const c = data[i];

            // a > b < c 조건을 확인 (b가 가장 낮은 저점)
            if (a.close > b.close && b.close < c.close) {
                if (b.close < lowest.price) {
                    lowest = { price: b.close, index: i - 1, date: b.date };
                }
            }
        }

        return lowest.index !== -1 ? lowest : null;
    };

    const findHighPoint = (data, period, startIndex) => {
        let highest = { price: -Infinity, index: -1, date: "" };

        for (let i = startIndex; i >= 2; i--) {
            if (i < period) break;

            const a = data[i - 2];
            const b = data[i - 1];
            const c = data[i];

            // a < b > c 조건을 확인 (b가 가장 높은 고점)
            if (a.close < b.close && b.close > c.close) {
                if (b.close > highest.price) {
                    highest = { price: b.close, index: i - 1, date: b.date };
                }
            }
        }

        return highest.index !== -1 ? highest : null;
    };

    return (
        <div>
            <FetchMACDData stockCode={stockCode} onMACDFetch={setMACDData} />
            <FetchStockDataForCode stockCode={stockCode} onSDFCFetch={setPriceData} />
            <h4 className="analysis-text">Divergence 분석</h4>
            <p dangerouslySetInnerHTML={{ __html: analysisText }} />
        </div>
    );
}

export default MACDAnalysis2;
