import React, {useState, useEffect} from 'react'
import FetchStochasticData from "../ComponentsFetch/FetchStochasticData";

function StochasticTotalCalculation({ stockCode, onResultUpdate }) {
    const [stochasticData, setStochasticData] = useState(null);

    useEffect(() => {
        if(stochasticData) {
            const { perK, score } = stochasticData;

            const recommendation = getRecommendation(score); // 받아온 점수에 따른 추천

            if(onResultUpdate) {
                onResultUpdate({
                    name: 'Stochastic %k(14, 3, 3)',
                    value: perK,
                    damm: score,
                    recommendation: recommendation
                });
            }
            console.log(perK, score)
        }
    },[stochasticData]);

    const getRecommendation = (score) => {
        if (score <= 2.0) {
            return '강한 매수';
        } else if (score <= 4.0) {
            return '매수';
        } else if (score <= 6.0) {
            return '보통';
        } else if (score <= 8.0) {
            return '매도';
        } else {
            return '강한 매도';
        }
    };

    return (
        <>
            <FetchStochasticData stockCode={stockCode} onStochasticFetch={(result) => setStochasticData(result)}/>
        </>
    );
}

export default StochasticTotalCalculation;
