import React, {useState, useEffect} from "react";

function RSIAnalysis2({ stockCode, stockData, rsiData }) {
    const [bullishDivergence , setBullishDivergence ] = useState([])
    const [bearishDivergence, setBearishDivergence] = useState([])


    const calculateBullishDivergence = (stockCode, stockData, rsiData) => {
        const filteredStockData = stockData.filter(data => data.stock_code === stockCode);
        const sortedStockData = [...filteredStockData].sort((a, b) => new Date(a.date) - new Date(b.date));

        let lastLow = null;
        let previousLow = null;
        let lastLowRsi = null;
        let previousLowRsi = null;

        for (let i = 1; i < sortedStockData.length - 1; i++) {
            if (sortedStockData[i - 1].close > sortedStockData[i].close && sortedStockData[i].close < sortedStockData[i + 1].close) {
                if (!previousLow) {
                    previousLow = { date: sortedStockData[i].date, close: sortedStockData[i].close };
                    previousLowRsi = rsiData.find(rsi => rsi.date === previousLow.date)?.RSI;
                } else if (!lastLow) {
                    lastLow = { date: sortedStockData[i].date, close: sortedStockData[i].close };
                    lastLowRsi = rsiData.find(rsi => rsi.date === lastLow.date)?.RSI;
                    break; // 두 저점을 찾았으므로 종료
                }
            }
        }

        if (!previousLow || !lastLow || lastLowRsi === null || previousLowRsi === null) return false;

        const isBullishDivergence = lastLow.close < previousLow.close && lastLowRsi > previousLowRsi;

        return isBullishDivergence ? { lastLow, previousLow } : false;
    };


    const calculateBearishDivergence = (stockCode, stockData, rsiData) => {

        const filteredStockData = stockData.filter(data => data.stock_code === stockCode)

        const sortedStockData = [...filteredStockData].sort((a, b) => b.close - a.close);
        let firstHighClose = sortedStockData[1];
        let secondHighClose = sortedStockData[0];

        if (new Date(secondHighClose.date) < new Date(firstHighClose.date)) {
            [firstHighClose, secondHighClose] = [secondHighClose, firstHighClose]
        }
        const sortedStockRsi = [...rsiData].sort((a, b) => b.RSI - a.RSI);
        let firstHighRsi = sortedStockRsi[0];
        let secondHighRsi = sortedStockRsi[1];

        if (new Date(secondHighRsi.date) < new Date(firstHighRsi.date)) {
            [firstHighRsi, secondHighRsi] = [secondHighRsi, firstHighRsi];
        }

        return secondHighClose.close > firstHighClose.close &&
            secondHighRsi.RSI < firstHighRsi.RSI;
    }

    useEffect(() => {
        const bullishDivergence = calculateBullishDivergence(stockCode, stockData, rsiData);
        const bearishDivergence = calculateBearishDivergence(stockCode, stockData, rsiData);

        setBullishDivergence(bullishDivergence);
        setBearishDivergence(bearishDivergence);

    }, [stockCode, stockData, rsiData]);

    return (
        <div>
            <h5>강세 다이버전스</h5>
            {bullishDivergence ? (
                <p style={{color: 'green'}}>강세 다이버전스가 감지되었습니다: 매수 신호<br></br></p>
            ) : (
                <p>강세 다이버전스가 감지되지 않았습니다.</p>
            )}

            <h5><br></br>약세 다이버전스</h5>
            {bearishDivergence ? (
                <p style={{ color: 'red' }}>약세 다이버전스가 감지되었습니다: 매도 신호</p>
            ) : (
                <p>약세 다이버전스가 감지되지 않았습니다.</p>
            )}
        </div>
    );
}
export default RSIAnalysis2
