import React, {useEffect, useState} from "react";
import FetchMACDData from "../fetchcomponents/FetchMACDData";
import FetchStockDataForCode from "../fetchcomponents/FetchStockDataForCode";
import FetchStockList from "../fetchcomponents/FetchStockList";
import Highcharts from "highcharts";
import HighchartsReact from "highcharts-react-official";

function ChartOfMACD({ stockCode }) {
    const [stockData, setStockData] = useState([]);
    const [stockList, setStockList] = useState([]);
    const [MACDData, setMACDData] = useState([]);
    const [options, setOptions] = useState({});

    useEffect(() => {
        if (stockData.length > 0 && stockList.length > 0 && MACDData.length > 0) {
            const MACDLine = MACDData.map(item => [new Date(item.date).getTime(), item.MACD]);
            const signalLine = MACDData.map(item => [new Date(item.date).getTime(), item.Signal]);
            const histogram = MACDData.map(item => [new Date(item.date).getTime(), item.Histogram]);

            setOptions({
                chart: {
                    type: 'line',
                    height: 210,
                },
                title: {
                    text: ``,
                },
                accessibility: {
                    enabled: false // 접근성 모듈 비활성화
                },
                xAxis: {
                    type: 'datetime',
                    labels: {
                        formatter: function () {
                            return Highcharts.dateFormat('%m. %d', this.value);
                        }
                    },
                },
                yAxis: [{
                    title: {
                        text: '',
                    },
                }, {
                    title: {
                        text: '',
                    },
                    opposite: true,
                }],
                series: [
                    {
                        name: 'MACD Line',
                        data: MACDLine,
                        color: '#0071e3',
                    },
                    {
                        name: 'Signal Line',
                        data: signalLine,
                        color: '#FF0000',
                    },
                    {
                        name: 'Histogram',
                        type: 'column',
                        data: histogram,
                        yAxis: 1,
                        color: '#00FF00',
                    },
                ],
                tooltip: {
                    shared: true,
                    crosshairs: true,
                },
                credits: {
                    enabled: false,
                },
                legend: {
                    enabled: true,
                },
            });
        }
    }, [stockData, stockList, MACDData]);

    return (
        <div>
            <FetchStockDataForCode stockCode={stockCode} onSDFCFetch={setStockData} />
            <FetchMACDData stockCode={stockCode} onMACDFetch={setMACDData} />
            <FetchStockList onListFetch={setStockList} />

            {options.series && options.series.length > 0 && (
                <HighchartsReact highcharts={Highcharts} options={options} />
            )}
        </div>
    );
}

export default ChartOfMACD;
