import React, {useState} from 'react';
import { components } from 'react-select';
import { useNavigate } from 'react-router-dom';
import './CustomOption.css'

const CustomOption = (props) => {
    const navigate = useNavigate();
        const [filteredData, setFilteredData] = useState([]); // 필터링된 데이터



    const handlePageExit = () => {
        const stockCodes = filteredData.map(stock => stock.stock_code); // 모든 stock_code 가져오기
        console.log("Sending stock codes:", stockCodes); // 전송할 데이터 확인

        fetch('http://127.0.0.1:8000/api/delete_home_stock_codes/', {
            method: 'POST',  // POST로 설정
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ stock_codes: stockCodes }) // JSON 형식으로 데이터 전송
        }).then(response => {
            console.log("Delete request sent:", response.status); // 응답 상태 코드 확인
            return response.json();
        }).then(data => {
            console.log("Response data:", data); // 응답 데이터 확인
        }).catch(error => {
            console.log("Error sending delete request:", error);
        });
    };

    // 버튼을 눌러 다른 페이지로 이동할 때 데이터 삭제
    const navigateToComprehensiveAnalysis = (stockCode, stockName) => {
        handlePageExit();  // 페이지 이동 전에 데이터 삭제
        navigate('/comprehensiveanalysis', {
            state: {
                stockCode: stockCode,
                stockName: stockName
            }
        });
    };

    return (
        <components.Option {...props}>
            <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                <span>{props.data.label}</span> {/* 기본적으로 종목명을 표시 */}
                <div>
                    {/* 1번 버튼 (선택 시 동작) */}
                    <button
                        style={{ marginRight: '5px' }}
                        onClick={() => {
                            props.selectOption(props.data);
                        }} className="select-research-button">
                        선택
                    </button>

                    {/* 2번 버튼 (종합 분석 페이지로 이동) */}
                    <button
                        onClick={(e) => {
                            e.stopPropagation(); // 부모 클릭 이벤트가 실행되지 않도록 방지
                            navigateToComprehensiveAnalysis(data.stock_code, data.stock_name);
                        }} className="select-research-button">
                        분석
                    </button>
                </div>
            </div>
        </components.Option>
    );
};

export default CustomOption;
