import React, {uesState, useState} from 'react'
import RSICalculations1 from "./RSICalculations1";

function TotalCalculation({ stockCode }) {
    const [rsiScore, setRSIScore] = useState(null);

    const totalScore = rsiScore ;

        const getRecommendation = (totalScore) => {
        if (totalScore <= 2.0) {
            return '강한 매수';
        } else if (totalScore <= 4.0) {
            return '매수';
        } else if (totalScore <= 6.0) {
            return '보통';
        } else if (totalScore <= 8.0) {
            return '매도';
        } else {
            return '강한 매도';
        }
    };

    const finalRecommendation = getRecommendation(totalScore);

    return (
        <div>
            <h3>종합 분석 결과</h3>
            <RSICalculations1 stockCode={stockCode} onScoreCalculated={setRSIScore} />
            <p>최종 추천: {finalRecommendation}</p>
        </div>
    );
}

export default TotalCalculation;
