import React, { useState, useEffect } from "react";
import FetchMFIData from "../ComponentsFetch/FetchMFIData";
import FetchStockDataForCode from "../ComponentsFetch/FetchStockDataForCode";
import HighchartsReact from "highcharts-react-official";
import Highcharts from "highcharts";
import HighchartsStock from "highcharts/modules/stock"; // Highcharts Stock 모듈 추가

// Highcharts 모듈 활성화
HighchartsStock(Highcharts);

function ChartOfMFI({ stockCode }) {
    const [stockData, setStockData] = useState([]);
    const [MFIData, setMFIData] = useState([]);
    const [options, setOptions] = useState({});

    useEffect(() => {
        if (stockData.length > 0 && MFIData.length > 0 && stockCode) {
            // MFI 데이터 포맷팅
            const formattedMFIData = MFIData.map(item => [
                new Date(item.date).getTime(),
                item.MFI
            ]);

            // Volume 데이터 포맷팅
            const formattedVolumeData = stockData.map(item => ({
                x: new Date(item.date).getTime(),
                y: item.volume,
            }));

            // Close Price 데이터 포맷팅
            const formattedClosePriceData = stockData.map(item => ({
                x: new Date(item.date).getTime(),
                y: item.close,
            }));

            // 차트 옵션 설정
            setOptions({
                chart: {
                    height: 600, // 차트 전체 높이 설정
                    spacing: [10, -70, 10, 10],
                    zoomType: 'x', // x축을 기준으로 확대 가능
                    events: {
                        render: function () {
                            // Close Price 텍스트의 위치 강제 조정
                            this.yAxis[0].axisTitle.attr({
                                x: this.plotLeft +40,
                                y: this.plotTop +20
                            });

                            // Volume 텍스트의 위치 강제 조정
                            this.yAxis[1].axisTitle.attr({
                                x: this.plotLeft + 30,
                                y: this.plotTop + (this.plotHeight * 0.85) + 7
                            });

                            // MFI 텍스트의 위치 강제 조정
                            this.yAxis[2].axisTitle.attr({
                                x: this.plotLeft + 40,
                                y: this.plotTop + (this.plotHeight * 0.55) + 7
                            });

                        }
                    }
                },
                title: {
                    text: ' ' // 차트 제목 비움
                },
                accessibility: {
                    enabled: false // 접근성 비활성화
                },
                xAxis: {
                    type: 'datetime', // x축은 날짜 형식
                    crosshair: true, // 마우스 호버 시 십자선 표시
                },
                yAxis: [
                    {
                        title: {
                            text: 'Close Price', // Close Price용 y축 제목
                            style: {
                                fontSize: '12px', // 폰트 크기
                            },
                            rotation: 0, // 수평 방향으로 제목 회전

                        },
                        height: '50%', // Close Price 차트는 상단 50% 영역에 위치
                        lineWidth: 2,
                        opposite: true, // 왼쪽에 위치
                    },
                    {
                        title: {
                            text: 'Volume', // Volume용 y축 제목
                            style: {
                                fontSize: '12px', // 폰트 크기
                            },
                            rotation: 0, //
                        },
                        top: '52%',
                        height: '18%',
                        offset: 0,
                        lineWidth: 2,
                        labels: {
                            enabled: false // 볼륨에 라벨 비활성화
                        },
                        opposite: true, // 왼쪽에 위치
                    },
                    {
                        title: {
                            text: 'MFI(20, 80)', // MFI용 y축 제목
                            style: {
                                fontSize: '12px', // 폰트 크기
                            },
                            rotation: 0, // 수평 방향으로 제목 회전

                        },
                        top: '72%',
                        height: '28%',
                        offset: 0,
                        lineWidth: 2,
                        min: 0,
                        max: 100,
                        tickPositions: [5, 20, 80, 95],
                        plotBands: [{
                            from: 20,
                            to: 80,
                            color: 'rgba(144, 238, 144, 0.1)' // MFI의 20~80 구간에 반투명 색상 추가
                        }],
                        labels: {
                            formatter: function () {
                                if (this.value === 5 || this.value === 95) {
                                    return '';
                                }
                                return this.value;
                            }
                        },
                        plotLines: [
                            {
                                value: 80,
                                color: 'gray',
                                dashStyle: 'dash',
                                width: 1,
                                zIndex: 5,
                            },
                            {
                                value: 20,
                                color: 'gray',
                                dashStyle: 'dash',
                                width: 1,
                                zIndex: 5,
                            }
                        ],
                        opposite: true, // 왼쪽에 위치
                    }
                ],
                series: [
                    {
                        name: 'Close Price',
                        type: 'line',
                        data: formattedClosePriceData, // Close Price 데이터
                        marker: {
                            enabled: false,
                        },
                        yAxis: 0, // 첫 번째 yAxis (Close Price)
                        color: '#0071e3', // Close Price 라인 색상
                        lineWidth: 1.5,
                    },
                    {
                        name: 'Volume',
                        type: 'column',
                        data: formattedVolumeData, // Volume 데이터
                        yAxis: 1, // 세 번째 yAxis (Volume)
                        color: '#A9A9A9', // Volume 컬럼 색상
                        pointWidth: 1,
                    },
                    {
                        name: 'MFI',
                        type: 'line',
                        data: formattedMFIData, // MFI 데이터
                        marker: {
                            enabled: false,
                        },
                        yAxis: 2, // 두 번째 yAxis (MFI)
                        color: '#66CC75', // MFI 라인 색상
                        lineWidth: 1.5,
                    },
                ],
                legend: {
                    enabled: true // 범례 활성화
                },
                credits: {
                    enabled: false // "Highcharts.com" 텍스트 비활성화
                },
                tooltip: {
                    shared: true, // 툴팁 공유
                    crosshairs: true, // 십자선 표시
                    formatter: function () {
                        let tooltipHtml = `<b>${Highcharts.dateFormat('%m-%d', this.x)}</b><br/>`;
                        this.points.forEach(point => {
                            tooltipHtml += `${point.series.name}: <b>${point.y.toFixed(2)}</b><br/>`;
                        });
                        return tooltipHtml;
                    }
                },
                rangeSelector: {
                    enabled: true, // 범위 선택 옵션 활성화
                    inputEnabled: false,
                    buttons: [
                        {
                            type: 'month',
                            count: 1,
                            text: '1m'
                        },
                        {
                            type: 'month',
                            count: 3,
                            text: '3m'
                        },
                        {
                            type: 'month',
                            count: 6,
                            text: '6m'
                        },
                        {
                            type: 'ytd',
                            text: 'YTD'
                        },
                        {
                            type: 'year',
                            count: 1,
                            text: '1y'
                        },
                        {
                            type: 'all',
                            text: 'All'
                        }
                    ],
                    selected: 5 // Default 선택 범위 설정 (3개월)
                },
                scrollbar: {
                    enabled: true, // 하단 스크롤바 활성화
                }
            });
        }
    }, [stockData, MFIData, stockCode]);

    return (
        <div>
            <FetchStockDataForCode stockCode={stockCode} onSDFCFetch={setStockData} />
            <FetchMFIData stockCode={stockCode} onMFIFetch={setMFIData} />
            {options.series && options.series.length > 0 && (
                <HighchartsReact highcharts={Highcharts} options={options} />
            )}
        </div>
    );
}

export default ChartOfMFI;
