import React, {useEffect, useState} from "react";
import FetchMACDData from "../ComponentsFetch/FetchMACDData";
import FetchStockDataForCode from "../ComponentsFetch/FetchStockDataForCode";
import FetchStockList from "../ComponentsFetch/FetchStockList";
import Highcharts from "highcharts";
import HighchartsReact from "highcharts-react-official";

function ChartOfMACD({ stockCode }) {
    const [stockData, setStockData] = useState([]);
    const [stockList, setStockList] = useState([]);
    const [MACDData, setMACDData] = useState([]);
    const [options, setOptions] = useState({});

    useEffect(() => {
        if (stockData.length > 0 && stockList.length > 0 && MACDData.length > 0) {

            const formattedMACDData = MACDData.map(item => {
                const time = new Date(item.date).getTime();
                return {
                    time,
                    MACDLine: item.MACD,
                    SignalLine: item.Signal,
                    Histogram: item.Histogram,
                };
            });

            const formattedClosePriceData =  stockData.map(item => ({
                x: new Date(item.date).getTime(),
                y: item.close,
            }));

            const formattedVolumeData = stockData.map(item => ({
                x: new Date(item.date).getTime(),
                y: item.volume,
            }))

            setOptions({
                chart: {
                    height: 600,
                    spacing: [10, -70, 10, 10],
                    zoomType: 'x',
                },
                title: {
                    text: ``,
                },
                accessibility: {
                    enabled: false // 접근성 모듈 비활성화
                },
                xAxis: {
                    type: 'datetime',
                    labels: {
                        formatter: function () {
                            return Highcharts.dateFormat('%m. %d', this.value);
                        }
                    },
                    crosshair: true, // 마우스 호버 시 십자선 표시
                },
                yAxis: [
                    {
                        title: {
                            text: 'Close Price',
                            style: {
                                fontSize: '12px'
                            },
                            rotation: 0,
                        },
                        height: '50%',
                        lineWidth: 2,
                        opposite: true,
                    },
                    {
                        title: {
                            text: 'Volume',
                            style: {
                                fontSize: '12px',
                            },
                            rotation: 0,
                        },
                        top: '52%',
                        height: '18%',
                        offset: 0,
                        lineWidth: 2,
                        labels: {
                            enabled: false
                        },
                        opposite: true,
                    },
                    {
                        title: {
                            text: 'MACD',
                        },
                        top: '82%',
                        height: '18%',
                        offset: 0,
                        lineWidth: 2,
                        opposite: true
                    }
                ],
                series: [
                    {
                        name: 'Close Price',
                        type: 'line',
                        date: formattedClosePriceData,
                        market: {
                            enabled: false,
                        },
                        yAxis: 0,
                        color: '#0071e3',
                        lineWidth:1.5,
                    },
                    {
                        name: 'Volume',
                        type: 'column',
                        date: formattedVolumeData,
                        yAxis: 1,
                        color: '#A9A9A9',
                        pointWidth: 1.5,
                    },
                    {
                        name: 'MACD Line',
                        date: formattedMACDData.map(item => [item.time, item.MACDLine]),
                        type: 'line',
                        yAxis: 2,
                        color: '#0071e3',
                        lineWidth: 1.5,
                    },
                    {
                        name: 'Signal Line',
                        date: formattedMACDData.map(item => [item.time, item.SignalLine]),
                        type: 'line',
                        yAxis: 2,
                        color: '#FF0000',
                        lineWidth: 1.5,
                    },
                    {
                        name: 'Histogram',
                        date: formattedMACDData.map(item => [item.time, item.Histogram]),
                        type: 'column',
                        yAxis: 2,
                        color: '#00FF00',
                        pointWidth: 1.5,
                    }
                ],
                tooltip: {
                    shared: true, // 툴팁 공유
                    crosshairs: true, // 십자선 표시
                    formatter: function () {
                        let tooltipHtml = `<b>${Highcharts.dateFormat('%m-%d', this.x)}</b><br/>`;
                        this.points.forEach(point => {
                            tooltipHtml += `${point.series.name}: <b>${point.y.toFixed(2)}</b><br/>`;
                        });
                        return tooltipHtml;
                    }
                },
                credits: {
                    enabled: false,
                },
                legend: {
                    enabled: true,
                },
            });
        }
    }, [stockData, stockList, MACDData]);

    return (
        <div>
            <FetchStockDataForCode stockCode={stockCode} onSDFCFetch={setStockData} />
            <FetchMACDData stockCode={stockCode} onMACDFetch={setMACDData} />

            {options.series && options.series.length > 0 && (
                <HighchartsReact highcharts={Highcharts} options={options} />
            )}
        </div>
    );
}

export default ChartOfMACD;
