import React, { useState } from 'react';
import Navbar from 'components/Navbar';
import SearchableDropdown from './SearchableDropdown';
import Tabs from './Tabs';
import ChartSection from './ChartSection';
import AnalysisSection from './AnalysisSection';
import DataSection from './DataSection';
import FetchStockData from './components/FetchStockData';

function Home() {
    const [activeTab, setActiveTab] = useState('RSI');
    const [selectedStock, setSelectedStock] = useState(null);
    const [stockData, setStockData] = useState([]); 

    const handleDataFetch = (data) => {
        setStockData(data);
    };

    const availableStockCodes = [
        { code: 'AAPL', name: 'Apple' },
        { code: 'MSFT', name: 'Microsoft' },
        // 추가 종목 데이터를 여기에 추가
    ];

    const handleStockSelect = (stockCode) => {
        setSelectedStock(stockCode);
    };

    return (
        <div>
            <Navbar />
            <FetchStockData onDataFetch={handleDataFetch} />
            <div className="content">
                <SearchableDropdown options={availableStockCodes} onSelect={handleStockSelect} />
                <Tabs activeTab={activeTab} onTabClick={setActiveTab} />
                <ChartSection activeTab={activeTab} rsiData={[]} macdData={[]} />
                <AnalysisSection activeTab={activeTab} rsiAnalysis={{}} macdAnalysis={{}} />
                <DataSection stockName={selectedStock} stockData={stockData} />
            </div>
        </div>
    );
}

export default Home;
