import React, { useState, useEffect } from 'react';
import FetchMACDData from '../ComponentsFetch/FetchMACDData';

function MACDAnalysis5({ stockCode }) {
    const [macdData, setMACDData] = useState([]);
    const [analysisResult, setAnalysisResult] = useState('');

    const analyzeMACDTrendStrength = (macdData, period = 5) => {
        if (macdData.length < period) {
            return '데이터가 충분하지 않습니다.';
        }

        const recentData = macdData.slice(-period);
        const macdChanges = recentData.map(data => data.MACD_Abs_Change);
        const avgMACDChange = macdChanges.reduce((a, b) => a + b, 0) / period;

        // 최근 MACD 변화량의 평균과 현재 MACD 변화량 비교
        const currentMACDChange = macdData[macdData.length - 1].MACD_Abs_Change;

        let result = '';

        if (currentMACDChange > avgMACDChange) {
            result += `현재 MACD 변화량이 최근 평균보다 큽니다 (${currentMACDChange.toFixed(2)} > ${avgMACDChange.toFixed(2)}).<br/>`;
            result += '추세의 힘이 강해지고 있습니다. 강한 모멘텀을 나타냅니다.<br/>';
        } else {
            result += `현재 MACD 변화량이 최근 평균보다 작습니다 (${currentMACDChange.toFixed(2)} ≤ ${avgMACDChange.toFixed(2)}).<br/>`;
            result += '추세의 힘이 약해지고 있습니다. 모멘텀이 둔화되고 있습니다.<br/>';
        }

        return result;
    };

    useEffect(() => {
        if (macdData.length > 0) {
            const result = analyzeMACDTrendStrength(macdData);
            setAnalysisResult(result);
        }
    }, [macdData]);

    return (
        <div>
            <FetchMACDData stockCode={stockCode} onMACDFetch={setMACDData} />
            <h4>MACD 추세의 힘 분석</h4>
            <p dangerouslySetInnerHTML={{ __html: analysisResult }}></p>
        </div>
    );
}

export default MACDAnalysis5;
