import React, { useState, useEffect } from 'react';
import FetchCCIData from '../ComponentsFetch/FetchCCIData';
import FetchStockDataForCode from '../ComponentsFetch/FetchStockDataForCode';

function CCIAnalysis1({ stockCode }) {
    const [cciData, setCCIData] = useState([]);
    const [stockData, setStockData] = useState([]);
    const [analysisResult, setAnalysisResult] = useState('');

    // CCI 분석 함수
    const analyzeCCI = (cciData) => {
        let lastIndex = cciData.length - 1;
        if (lastIndex < 1) return '데이터가 충분하지 않습니다.';

        const currentCCI = cciData[lastIndex].CCI.toFixed(2);
        const prevCCI = cciData[lastIndex - 1]?.CCI || 0;

        let result = `<strong>현재 CCI: ${currentCCI}</strong><br/>`;

        // 1. 과매수/과매도 상태 분석
        if (currentCCI >= 100) {
            result += '현재 시장은 과매수 상태에 있으며, 가격이 상승세에 있을 가능성이 큽니다. <br/>';
        } else if (currentCCI <= -100) {
            result += '현재 시장은 과매도 상태에 있으며, 가격이 하락세에 있을 가능성이 큽니다. <br/>';
        } else {
            result += '현재 시장은 중립 상태에 있으며, 큰 변동이 없을 수 있습니다. <br/>';
        }

        // 2. CCI 추세 변화 분석
        if (currentCCI > 100 && prevCCI <= 100) {
            result += 'CCI가 +100을 돌파하였습니다. 강한 상승 추세에 진입할 가능성이 있습니다.<br/>';
        } else if (currentCCI < -100 && prevCCI >= -100) {
            result += 'CCI가 -100을 하향 돌파하였습니다. 하락 추세에 진입할 가능성이 큽니다.<br/>';
        }

        // 3. CCI 추세 지속성 분석
        if (currentCCI > 100 && prevCCI > 100) {
            result += 'CCI가 +100 이상에서 유지되고 있습니다. 강세 시장이 지속될 가능성이 큽니다.<br/>';
        } else if (currentCCI < -100 && prevCCI < -100) {
            result += 'CCI가 -100 이하에서 유지되고 있습니다. 약세 시장이 지속될 가능성이 큽니다.<br/>';
        } else if (currentCCI > 0 && currentCCI < 100) {
            result += 'CCI가 상승 중이나 아직 과매수 상태는 아닙니다. 지속적인 상승 가능성이 있습니다.<br/>';
        } else if (currentCCI < 0 && currentCCI > -100) {
            result += 'CCI가 하락 중이나 아직 과매도 상태는 아닙니다. 하락 추세에 주의하세요.<br/>';
        }

        return result;
    };

    useEffect(() => {
        if (cciData.length > 0) {
            const result = analyzeCCI(cciData);
            setAnalysisResult(result);
        }
    }, [cciData]);

    return (
        <div>
            <FetchCCIData stockCode={stockCode} onCCIFetch={setCCIData} />
            <FetchStockDataForCode stockCode={stockCode} onSDFCFetch={setStockData} />
            <h4 className="analysis-text">CCI 분석</h4>
            <p dangerouslySetInnerHTML={{ __html: analysisResult.replace(/\n/g, '<br />') }}></p>
        </div>
    );
}

export default CCIAnalysis1;
