import React, { useState, useEffect } from 'react';
import Highcharts from 'highcharts';
import HighchartsReact from 'highcharts-react-official';
import HighchartsMore from 'highcharts/highcharts-more';
import HighchartsStock from 'highcharts/modules/stock';
import FetchStockDataForCode from '../ComponentsFetch/FetchStockDataForCode';
import FetchIchimokuData from '../ComponentsFetch/FetchIchimokuData';

// 모달 스타일 (간단한 팝업 모달 UI 구현)
const modalStyle = {
    position: 'fixed',
    top: '50%',
    left: '50%',
    transform: 'translate(-50%, -50%)',
    backgroundColor: 'white',
    padding: '20px',
    boxShadow: '0 5px 15px rgba(0,0,0,.5)',
    zIndex: 1000,
    width: '300px',
    borderRadius: '8px'
};

const overlayStyle = {
    position: 'fixed',
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    backgroundColor: 'rgba(0, 0, 0, 0.5)',
    zIndex: 999
};

// 모달 컴포넌트
function Modal({ show, onClose, children }) {
    if (!show) return null;
    return (
        <>
            <div style={overlayStyle} onClick={onClose}></div>
            <div style={modalStyle}>
                {children}
                <button onClick={onClose}>닫기</button>
            </div>
        </>
    );
}

HighchartsMore(Highcharts);
HighchartsStock(Highcharts);

function ChartWithModal({ stockCode }) {
    const [stockData, setStockData] = useState([]);
    const [ichimokuData, setIchimokuData] = useState([]);
    const [options, setOptions] = useState({});
    const [showModal, setShowModal] = useState(false); // 모달 상태
    const [enabledSeries, setEnabledSeries] = useState({
        tenkan: true,
        kijun: true,
        spanA: true,
        spanB: true,
        chikou: true,
        ichimokuPositive: true,
        ichimokuNegative: true,
    });

    useEffect(() => {
        if (stockData.length > 0 && ichimokuData.length > 0 && stockCode) {
            const dataForChart = stockData.map(item => ({
                date: new Date(item.date).getTime(),
                close: item.close
            })).sort((a, b) => new Date(a.date) - new Date(b.date));

            const tenkan_sen = ichimokuData.map(item => [new Date(item.date).getTime(), item.Tenkan_sen]);
            const kijun_sen = ichimokuData.map(item => [new Date(item.date).getTime(), item.Kijun_sen]);
            const senkou_Span_A = ichimokuData.map(item => [new Date(item.date).getTime(), item.Senkou_Span_A]);
            const senkou_Span_B = ichimokuData.map(item => [new Date(item.date).getTime(), item.Senkou_Span_B]);
            const chikou_Span = ichimokuData.map(item => [new Date(item.date).getTime(), item.Chikou_Span]);

            const CloudAreaPositive = ichimokuData.filter(item => item.Senkou_Span_A > item.Senkou_Span_B)
                .map(item => ({
                    x: new Date(item.date).getTime(),
                    low: item.Senkou_Span_B,
                    high: item.Senkou_Span_A
                }));


            const CloudAreaNegative = ichimokuData.filter(item => item.Senkou_Span_A <= item.Senkou_Span_B)
                .map(item =>({
                    x: new Date(item.date).getTime(),
                    low: item.Senkou_Span_A,
                    high: item.Senkou_Span_B,
                }))

            setOptions({
                chart: {
                    type: 'line',
                    height: 400
                },
                title: {
                    text: ``
                },
                xAxis: {
                    type: 'datetime',
                    labels: { format: '{value:%m-%d}' }
                },
                yAxis: { title: { text: '' } },
                series: [
                    {
                        name: 'Close Price',
                        data: dataForChart.map(item => [item.date, item.close]),
                        color: '#0071e3',
                        marker: { enabled: false },
                        lineWidth: 2,
                        visible: true
                    },
                    {
                        name: 'Tenkan-sen (전환선)',
                        data: tenkan_sen,
                        color: '#FF5733',
                        marker: { enabled: false },
                        lineWidth: 1,
                        visible: enabledSeries.tenkan
                    },
                    {
                        name: 'Kijun-sen (기준선)',
                        data: kijun_sen,
                        color: '#FFC300',
                        marker: { enabled: false },
                        lineWidth: 1,
                        visible: enabledSeries.kijun
                    },
                    {
                        name: 'Senkou Span A (선행스팬 A)',
                        data: senkou_Span_A,
                        color: '#33FF57',
                        marker: { enabled: false },
                        lineWidth: 1.5,
                        visible: enabledSeries.spanA
                    },
                    {
                        name: 'Senkou Span B (선행스팬 B)',
                        data: senkou_Span_B,
                        color: '#C70039',
                        marker: { enabled: false },
                        lineWidth: 1,
                        visible: enabledSeries.spanB
                    },
                    {
                        name: 'Chikou Span (후행스팬)',
                        data: chikou_Span,
                        color: '#8B4513',
                        marker: { enabled: false },
                        lineWidth: 1,
                        visible: enabledSeries.chikou
                    },
                    {
                        type: 'arearange',
                        name: 'Ichimoku Cloud Positive',
                        data: CloudAreaPositive,
                        marker: { enabled: false },
                        color: 'rgba(50, 205, 50, 0.2)', // 옅은 붉은색
                        enableMouseTracking: false, // 구름에 마우스 호버 시 툴팁 비활성화
                        visible: enabledSeries.ichimokuPositive
                    },
                    {
                        type: 'arearange',
                        name: 'Ichimoku Cloud Negative',
                        data: CloudAreaNegative,
                        marker: { enabled: false },
                        color: 'rgba(255, 99, 132, 0.3)', // 옅은 붉은색
                        enableMouseTracking: false, // 구름에 마우스 호버 시 툴팁 비활성화
                        visible: enabledSeries.ichimokuNegative
                    }
                ],
                legend: {
                    enabled: false // 범례 숨기기
                },
                credits: {
                    enabled: false // Disable the "Highcharts.com" text
                },
                plotOptions: {
                    series: {
                        states: {
                            inactive: {
                                opacity: 1 // 마우스를 올렸을 때 다른 라인의 투명도를 1로 설정해 희미해지지 않도록 설정
                            },
                            hover: {
                                enabled: false // 마우스 호버 시 희미해지는 기능 비활성화
                            }
                        }
                    }
                },
                tooltip: {
                    shared: false, // 툴팁이 공유되는 경우에도 희미해질 수 있으니 비활성화
                    crosshairs: false // 크로스헤어 기능도 끔
                },
                navigator: {
                    enabled: true, // 스크롤바를 통한 확대/축소 활성화
                },
                scrollbar: {
                    enabled: true, // 하단 스크롤바 활성화
                },
                rangeSelector: {
                    enabled: true, // 범위 선택 옵션 활성화
                    buttons: [
                        {
                            type: 'month',
                            count: 1,
                            text: '1m'
                        },
                        {
                            type: 'month',
                            count: 3,
                            text: '3m'
                        },
                        {
                            type: 'month',
                            count: 6,
                            text: '6m'
                        },
                        {
                            type: 'ytd',
                            text: 'YTD'
                        },
                        {
                            type: 'year',
                            count: 1,
                            text: '1y'
                        },
                        {
                            type: 'all',
                            text: 'All'
                        }
                    ],
                    selected: 5 // Default 선택 범위 설정 (3개월)
                },
            });
        }
    }, [stockData, ichimokuData, stockCode, enabledSeries]);

    // 시리즈 숨김/표시 핸들러
    const toggleSeriesVisibility = (key) => {
        setEnabledSeries((prev) => ({
            ...prev,
            [key]: !prev[key]
        }));
    };

    return (
        <div>
            <FetchStockDataForCode stockCode={stockCode} onSDFCFetch={setStockData} />
            <FetchIchimokuData stockCode={stockCode} onIchimokuFetch={setIchimokuData} />

            {/* 설정 버튼 클릭 시 모달 표시 */}
            <button onClick={() => setShowModal(true)}>설정 열기</button>

            {/* 모달 팝업 */}
            <Modal show={showModal} onClose={() => setShowModal(false)}>
                <h4>Ichimoku 설정</h4>
                <label>
                    <input
                        type="checkbox"
                        checked={enabledSeries.tenkan}
                        onChange={() => toggleSeriesVisibility('tenkan')}
                    />
                    Tenkan-sen (전환선)<br/>
                </label>
                <label>
                    <input
                        type="checkbox"
                        checked={enabledSeries.kijun}
                        onChange={() => toggleSeriesVisibility('kijun')}
                    />
                    Kijun-sen (기준선)<br/>
                </label>
                <label>
                    <input
                        type="checkbox"
                        checked={enabledSeries.spanA}
                        onChange={() => toggleSeriesVisibility('spanA')}
                    />
                    Senkou Span A (선행스팬 A)<br/>
                </label>
                <label>
                    <input
                        type="checkbox"
                        checked={enabledSeries.spanB}
                        onChange={() => toggleSeriesVisibility('spanB')}
                    />
                    Senkou Span B (선행스팬 B)<br/>
                </label>
                <label>
                    <input
                        type="checkbox"
                        checked={enabledSeries.chikou}
                        onChange={() => toggleSeriesVisibility('chikou')}
                    />
                    Chikou Span (후행스팬)<br/>
                </label>
                <label>
                    <input
                        type="checkbox"
                        checked={enabledSeries.ichimoku}
                        onChange={() => toggleSeriesVisibility('ichimoku')}
                    />
                    Ichimoku Cloud <br/>
                </label>
            </Modal>

            <HighchartsReact highcharts={Highcharts} options={options}/>
        </div>
    );
}

export default ChartWithModal;
