import React, {useState, useEffect} from 'react';
import FetchStockData from "../components/FetchStockData";
import FetchStockList from "../components/FetchStockList";
import '../styles/Navbar.css';
import './Home.css';


function Home() {
    const [stockData, setStockData] = useState([])
    const [stockList, setStockList] = useState([])
    const [filteredData, setFilteredData] = useState([])

    useEffect(() => {
        if(stockList.length > 0 && stockData > 0) {
            const latestDataMap = new Map()

            stockData.forEach((data) => {
                const existingData = latestDataMap.get(data.stock_code);
                if (!existingData || new Date(existingData.date) < new Date(data.date)) {
                    latestDataMap.set(data.stock_code, data)
                }
            });

            const filtered = stockList.filter(stock => latestDataMap.has(stock.code))
                                      .map(stock => latestDataMap.get(stock.code));

            setFilteredData(filtered);
        }
    }, [stockList, stockData]);


    return (
        <>
            <FetchStockList onListFetch={setStockList} />
            <FetchStockData onDataFetch={setStockData} />

            <div className='navbar'><h2>ProStock</h2></div>
            <div className='table-container'>
                <table className="table">
                    <thead>
                    <tr>
                        <th>종목명</th>
                        <th>일자</th>
                        <th>종가</th>
                        <th>고가</th>
                        <th>저가</th>
                        <th>시가</th>
                        <th>거래량</th>
                    </tr>
                    </thead>
                    <tbody>
                    {filteredData.map((data, index) => (
                        <tr key={index}>
                            <td>{data.stock_code}</td>
                            <td>{data.date}</td>
                            <td>{data.close}</td>
                            <td>{data.high}</td>
                            <td>{data.low}</td>
                            <td>{data.open}</td>
                            <td>{data.volume}</td>
                        </tr>
                    ))}
                    </tbody>
                </table>
            </div>
        </>
    );
}

export default Home;
