import React, { useState, useEffect } from "react";
import Highcharts from "highcharts";
import HighchartsReact from "highcharts-react-official";
import fetchRSIData from "./FetchRSIData"; // Adjust path as necessary

function ChartOfClose({ stockCode }) {
    const [stockData, setStockData] = useState([]);
    const [rsiData, setRsiData] = useState([]); // Added useState for RSI data
    const [filteredData, setFilteredData] = useState([]);
    const [options, setOptions] = useState({});

    useEffect(() => {
        const fetchStockData = async () => {
            try {
                const response = await fetch("http://127.0.0.1:8000/api/stock_data/");
                if (!response.ok) {
                    throw new Error("Failed to fetch data");
                }
                const data = await response.json();
                setStockData(data);
            } catch (err) {
                console.error("Error fetching stock data:", err);
            }
        };

        fetchStockData();
    }, []);

    useEffect(() => {
        const fetchRSIDataAsync = async () => {
            const rsiResult = await fetchRSIData(stockCode);
            setRsiData(rsiResult); // Correctly setting RSI data
        };

        if (stockCode) {
            fetchRSIDataAsync();
        }
    }, [stockCode]);

    useEffect(() => {
        if (stockData.length > 0 && rsiData.length > 0 && stockCode) {
            const dataForChart = stockData
                .filter(item => item.stock_code === stockCode)
                .filter(item => rsiData.some(rsi => rsi.date === item.date))  // 날짜가 일치하는 데이터만 필터링
                .map(item => ({
                    date: new Date(item.date).toLocaleDateString('ko-KR', { month: '2-digit', day: '2-digit' }),
                    close: item.close,
                }))
                .sort((a, b) => new Date(a.date) - new Date(b.date));

            setFilteredData(dataForChart);

            // Set chart options
            setOptions({
                chart: {
                    type: 'line',
                    height: 330,
                },
                title: {
                    text: '',
                },
                xAxis: {
                    categories: dataForChart.map(item => item.date),
                    labels: {
                        formatter: function () {
                            return this.value; // Keeping date format MM/DD
                        },
                    },
                },
                yAxis: {
                    title: {
                        text: 'Close Price',
                    },
                },
                series: [
                    {
                        name: 'Close Price',
                        data: dataForChart.map(item => item.close),
                        marker: {
                            enabled: false, // Disable data point markers
                        },
                        color: '#0071e3',
                    },
                ],
                legend: {
                    enabled: true,
                },
                credits: {
                    enabled: false, // Disable the "Highcharts.com" text
                },
                tooltip: {
                    shared: true,
                    crosshairs: true,
                },
            });
        }
    }, [stockData, rsiData, stockCode]);

    if (!stockCode) return null;

    return (
        <div>
            <HighchartsReact highcharts={Highcharts} options={options} />
        </div>
    );
}

export default ChartOfClose;
