import React, { useState, useEffect } from "react";
import Select from "react-select";
import FetchStockData from "../components/FetchStockData";
import FetchStockList from "../components/FetchStockList";
import './Detail.css';
import ProfileIcon from "../assets/profile.png";

function Detail() {
    const [stockData, setStockData] = useState([]);
    const [stockList, setStockList] = useState([]);
    const [availableStocks, setAvailableStocks] = useState([]);
    const [selectedStock, setSelectedStock] = useState(null);
    const [filteredData, setFilteredData] = useState([]);
    const [isMenuOpen, setIsMenuOpen] = useState(false);

    const toggleMenu = () => {
        setIsMenuOpen(!isMenuOpen);
    };

    // FetchStockData에서 모든 데이터를 가져옴
    const handleDataFetch = (data) => {
        setStockData(data); // 전체 데이터를 state에 저장
    };

    // FetchStockList에서 모든 종목 리스트를 가져옴
    const handleListFetch = (data) => {
        setStockList(data); // 전체 종목 리스트를 state에 저장
    };

    useEffect(() => {
        if (stockData.length > 0 && stockList.length > 0) {
            const available = stockList.filter(stock =>
                stockData.some(data => data.stock_code === stock.code)
            ).map(stock => ({
                value: stock.code,
                label: `${stock.name} (${stock.code})`
            }));
            setAvailableStocks(available);
        }
    }, [stockData, stockList]);

    const handleSelectChange = (selectedOption) => {
        setSelectedStock(selectedOption);

        // 선택된 종목에 따라 데이터를 필터링하여 표시
        const filtered = stockData.filter(data => data.stock_code === selectedOption.value)
                                  .map(data => ({
                                      date: data.date,
                                      close: data.close,
                                      high: data.high,
                                      low: data.low,
                                      open: data.open,
                                      volume: data.volume
                                  }));
        setFilteredData(filtered);
    };

    return (
        <>
            <div className='navbar'>
                <h2 className="navbar-title">ProStock</h2>
                <div className="profile-container" onClick={toggleMenu}>
                    <img src={ProfileIcon} alt="Profile" className="profile-icon"/>
                    {isMenuOpen && (
                        <div className="profile-menu">
                            <p>프로필</p>
                            <p>설정</p>
                            <p>로그아웃</p>
                        </div>
                    )}
                </div>
            </div>

            <div className="detail-container">
                <div className="detail-main-content">
                    <div className="detail-chart">
                        <FetchStockData onDataFetch={handleDataFetch}/>
                        <FetchStockList onListFetch={handleListFetch}/>
                        <h3>종목을 선택하세요</h3>
                        <Select
                            options={availableStocks}
                            onChange={handleSelectChange}
                            value={selectedStock}
                            placeholder="종목을 검색하세요..."
                        />
                        {selectedStock && (
                            <>
                                <h3 className="detail-chart-text">{selectedStock.label} 주가 차트</h3>
                                <ChartOfClose stockCode={selectedStock.value}/>
                            </>
                        )}
                    </div>

                    <div className="analysis-display">
                        <h3>분석</h3>
                        {/* 분석 데이터를 여기에 추가하세요 */}
                        <div className="textbox">
                            <h4 className="analysis-text">분석 1</h4>
                            <p>분석 내용...</p>
                        </div>
                    </div>
                </div>

                <div className="detail-table">
                    <h3>{selectedStock ? selectedStock.label : "종목"} 종목 데이터</h3>
                    <table>
                        <thead>
                        <tr>
                            <th>일자</th>
                            <th>종가</th>
                            <th>고가</th>
                            <th>저가</th>
                            <th>시가</th>
                            <th>거래량</th>
                        </tr>
                        </thead>
                        <tbody>
                        {filteredData.map((data, index) => (
                            <tr key={index}>
                                <td>{data.date}</td>
                                <td>{data.close}</td>
                                <td>{data.high}</td>
                                <td>{data.low}</td>
                                <td>{data.open}</td>
                                <td>{data.volume}</td>
                            </tr>
                        ))}
                        </tbody>
                    </table>
                </div>
            </div>
        </>
    );
}

export default Detail;
